function calculateIndex(inputs) {
  const { voterTurnout, volunteerRate, activismParticipation, socialCapitalScore, crimeRateChange, economicGrowth, accessToGreenSpace, publicServiceSatisfaction } = inputs;

  // Input validation and normalization to a 0-100 scale
  const normalize = (value, min, max) => Math.max(0, Math.min(100, ((value - min) / (max - min)) * 100));

  // Civic Engagement Metrics (already percentages 0-100)
  const normVoterTurnout = Math.max(0, Math.min(100, voterTurnout));
  const normVolunteerRate = Math.max(0, Math.min(100, volunteerRate));
  const normActivismParticipation = Math.max(0, Math.min(100, activismParticipation));

  // Community Wellbeing Metrics
  const normSocialCapital = normalize(socialCapitalScore, 1, 10);

  // Crime Rate Change: Lower is better, scale -20 to 20. Normalize so 20 is 0 and -20 is 100.
  const normCrimeRate = normalize(crimeRateChange, 20, -20); 

  // Economic Growth: Scale -10 to 10. Normalize so -10 is 0 and 10 is 100.
  const normEconomicGrowth = normalize(economicGrowth, -10, 10);

  // Access to Green Space: Already percentage 0-100
  const normAccessToGreenSpace = Math.max(0, Math.min(100, accessToGreenSpace));

  // Public Service Satisfaction: Scale 1 to 10. Normalize so 1 is 0 and 10 is 100.
  const normPublicServiceSatisfaction = normalize(publicServiceSatisfaction, 1, 10);

  // Calculate Civic Engagement Score (CES)
  // Weights: Voter Turnout (0.35), Volunteer Rate (0.35), Activism Participation (0.30)
  const civicEngagementScore = (
    normVoterTurnout * 0.35 +
    normVolunteerRate * 0.35 +
    normActivismParticipation * 0.30
  );

  // Calculate Community Wellbeing Indicator Score (CWIS)
  // Weights: Social Capital (0.25), Crime Rate (0.20), Economic Growth (0.20), Green Space Access (0.15), Public Service Satisfaction (0.20)
  const communityWellbeingScore = (
    normSocialCapital * 0.25 +
    normCrimeRate * 0.20 +
    normEconomicGrowth * 0.20 +
    normAccessToGreenSpace * 0.15 +
    normPublicServiceSatisfaction * 0.20
  );

  // Calculate Overall Civic Engagement Community Wellbeing Index (CEWI)
  // Weights: Civic Engagement Score (0.40), Community Wellbeing Indicator Score (0.60)
  const overallIndex = (
    civicEngagementScore * 0.40 +
    communityWellbeingScore * 0.60
  );

  // Apply a cap/floor to the final index if necessary, though normalized inputs should keep it within 0-100.
  const finalOverallIndex = Math.max(0, Math.min(100, overallIndex));

  return {
    civicEngagementScore: parseFloat(civicEngagementScore.toFixed(2)),
    communityWellbeingScore: parseFloat(communityWellbeingScore.toFixed(2)),
    overallIndex: parseFloat(finalOverallIndex.toFixed(2))
  };
}
