const { unrestDurationDays, affectedBusinessesCount, avgDailyRevenueLossPerBusiness, totalPropertyDamageEstimate, dailyPublicSectorResponseCost, dailyTourismRevenueImpact, dailySupplyChainDisruptionCost, longTermEconomicContractionFactor } = inputs;

// Input validation and edge case handling
if (unrestDurationDays <= 0 || !Number.isFinite(unrestDurationDays)) {
    throw new Error("Duration of Unrest must be a positive number of days.");
}
if (affectedBusinessesCount < 0 || !Number.isFinite(affectedBusinessesCount)) {
    throw new Error("Number of Affected Businesses cannot be negative.");
}
if (avgDailyRevenueLossPerBusiness < 0 || !Number.isFinite(avgDailyRevenueLossPerBusiness)) {
    throw new Error("Average Daily Revenue Loss cannot be negative.");
}
if (totalPropertyDamageEstimate < 0 || !Number.isFinite(totalPropertyDamageEstimate)) {
    throw new Error("Total Property Damage Estimate cannot be negative.");
}
if (dailyPublicSectorResponseCost < 0 || !Number.isFinite(dailyPublicSectorResponseCost)) {
    throw new Error("Daily Public Sector Response Cost cannot be negative.");
}
if (dailyTourismRevenueImpact < 0 || !Number.isFinite(dailyTourismRevenueImpact)) {
    throw new Error("Daily Tourism Revenue Loss cannot be negative.");
}
if (dailySupplyChainDisruptionCost < 0 || !Number.isFinite(dailySupplyChainDisruptionCost)) {
    throw new Error("Daily Supply Chain Disruption Cost cannot be negative.");
}
if (longTermEconomicContractionFactor < 1 || !Number.isFinite(longTermEconomicContractionFactor)) {
    throw new Error("Long-Term Economic Contraction Factor must be 1 or greater.");
}

// Cap unrest duration to prevent extreme, unrealistic calculations, e.g., 365 days max for this specific tool's scope
const effectiveUnrestDurationDays = Math.min(unrestDurationDays, 365);

// --- Calculate Direct Costs ---
// 1. Lost Business Revenue: Direct loss of sales/operations for affected businesses.
const lostBusinessRevenue = affectedBusinessesCount * avgDailyRevenueLossPerBusiness * effectiveUnrestDurationDays;

// 2. Property Damage: Sum of physical damage to private and public property.
//    'totalPropertyDamageEstimate' is assumed to be the aggregate figure.
const propertyDamageCost = totalPropertyDamageEstimate;

// 3. Public Sector Response Costs: Costs for law enforcement, emergency services, cleanup, etc.
const publicSectorResponseCosts = dailyPublicSectorResponseCost * effectiveUnrestDurationDays;

const totalDirectCosts = lostBusinessRevenue + propertyDamageCost + publicSectorResponseCosts;

// --- Calculate Indirect Costs ---
// 1. Tourism Revenue Loss: Impact on the regional tourism sector due to perception, travel advisories, etc.
const tourismRevenueLoss = dailyTourismRevenueImpact * effectiveUnrestDurationDays;

// 2. Supply Chain Disruption Costs: Broader economic costs from disrupted logistics, delayed goods, increased operational costs.
const supplyChainDisruptionCosts = dailySupplyChainDisruptionCost * effectiveUnrestDurationDays;

const totalIndirectCosts = tourismRevenueLoss + supplyChainDisruptionCosts;

// --- Calculate Total Economic Impact ---
// Immediate Impact: Sum of direct and immediate indirect costs.
const immediateEconomicImpact = totalDirectCosts + totalIndirectCosts;

// Long-Term Impact: Apply a multiplier to account for ripple effects such as deterred investment, slower recovery,
//                   loss of confidence, and other cascading effects not captured in daily figures.
const totalEconomicImpact = immediateEconomicImpact * longTermEconomicContractionFactor;

return {
    totalDirectCosts: parseFloat(totalDirectCosts.toFixed(2)),
    totalIndirectCosts: parseFloat(totalIndirectCosts.toFixed(2)),
    totalEconomicImpact: parseFloat(totalEconomicImpact.toFixed(2))
};
