function calculateROI(inputs) {
    const initialInvestment = inputs.initialInvestment;
    const annualOperatingMaintenance = inputs.annualOperatingMaintenance;
    const projectLifespan = inputs.projectLifespan;
    const annualAvoidedDamages = inputs.annualAvoidedDamages;
    const annualCoBenefits = inputs.annualCoBenefits;
    const annualProjectRevenue = inputs.annualProjectRevenue;
    const discountRate = inputs.discountRate / 100; // Convert percentage to decimal

    // Input validation for critical positive values
    if (initialInvestment <= 0 || projectLifespan <= 0) {
        return {
            netPresentValue: 0,
            returnOnInvestment: 0,
            benefitCostRatio: 0,
            paybackPeriod: "N/A"
        };
    }

    let totalDiscountedBenefits = 0; // Sum of discounted (avoided damages + co-benefits + revenue)
    let totalDiscountedOperatingCosts = 0; // Sum of discounted annual operating costs

    // For payback period and NPV, we need annual net cash flows
    let currentCumulativeNetBenefitsForPayback = -initialInvestment;
    let paybackPeriod = "N/A";

    let netPresentValueCashFlows = [-initialInvestment]; // Initial investment is a negative cash flow at year 0

    for (let year = 1; year <= projectLifespan; year++) {
        const grossAnnualBenefit = annualAvoidedDamages + annualCoBenefits + annualProjectRevenue;
        const netAnnualBenefit = grossAnnualBenefit - annualOperatingMaintenance;

        let discountFactor = 1;
        if (discountRate > 0) {
            discountFactor = 1 / Math.pow(1 + discountRate, year);
        }

        const discountedGrossAnnualBenefit = grossAnnualBenefit * discountFactor;
        const discountedAnnualOperatingCost = annualOperatingMaintenance * discountFactor;
        const discountedNetAnnualBenefit = netAnnualBenefit * discountFactor;

        totalDiscountedBenefits += discountedGrossAnnualBenefit;
        totalDiscountedOperatingCosts += discountedAnnualOperatingCost;
        netPresentValueCashFlows.push(discountedNetAnnualBenefit);

        // Update cumulative for payback calculation
        if (paybackPeriod === "N/A") { // Only update if payback hasn't been found yet
            currentCumulativeNetBenefitsForPayback += discountedNetAnnualBenefit;
            if (currentCumulativeNetBenefitsForPayback >= 0) {
                paybackPeriod = year; // Found the year when cumulative benefits become positive
            }
        }
    }

    // Calculate Net Present Value (NPV)
    const netPresentValue = netPresentValueCashFlows.reduce((acc, val) => acc + val, 0);

    // Total discounted costs including initial investment
    const totalDiscountedProjectCosts = initialInvestment + totalDiscountedOperatingCosts;

    // Calculate Return on Investment (ROI)
    let returnOnInvestment = 0;
    if (totalDiscountedProjectCosts > 0) {
        returnOnInvestment = (netPresentValue / totalDiscountedProjectCosts) * 100; // ROI as (NPV / Total Costs) * 100
    }

    // Calculate Benefit-Cost Ratio (BCR)
    let benefitCostRatio = 0;
    if (totalDiscountedProjectCosts > 0) {
        benefitCostRatio = totalDiscountedBenefits / totalDiscountedProjectCosts;
    }

    // Finalize Payback Period string
    if (paybackPeriod === "N/A" && netPresentValue >= 0) {
        paybackPeriod = "Within Project Lifespan"; // Project is profitable overall but payback may occur exactly at end or just slightly before
    } else if (paybackPeriod === "N/A") { // NPV is negative
        paybackPeriod = "Never";
    }

    return {
        netPresentValue: netPresentValue,
        returnOnInvestment: returnOnInvestment,
        benefitCostRatio: benefitCostRatio,
        paybackPeriod: paybackPeriod
    };
}
