function calculateROI(inputs) {
  const { 
    initialMitigationCost,
    annualMaintenanceCost,
    annualDamageWithoutMitigation,
    annualDamageWithMitigation,
    hazardProbabilityWithout,
    hazardProbabilityWith,
    currentInsurancePremium,
    postMitigationInsurancePremium,
    propertyValueAppreciation,
    timeHorizon
  } = inputs;

  // Input validation and default to 0 for non-positive values where applicable
  const initialCost = Math.max(0, initialMitigationCost || 0);
  const annualMaintCost = Math.max(0, annualMaintenanceCost || 0);
  const damageWithout = Math.max(0, annualDamageWithoutMitigation || 0);
  const damageWith = Math.max(0, annualDamageWithMitigation || 0);
  const probWithout = Math.max(0, Math.min(1, hazardProbabilityWithout || 0));
  const probWith = Math.max(0, Math.min(1, hazardProbabilityWith || 0));
  const currentPremium = Math.max(0, currentInsurancePremium || 0);
  const postPremium = Math.max(0, postMitigationInsurancePremium || 0);
  const propValueApprecAnnual = Math.max(0, propertyValueAppreciation || 0);
  const horizon = Math.max(1, timeHorizon || 1); // Time horizon must be at least 1 year

  // Ensure damageWith is not greater than damageWithout
  const effectiveDamageWith = Math.min(damageWith, damageWithout);

  // Ensure postPremium is not greater than currentPremium
  const effectivePostPremium = Math.min(postPremium, currentPremium);

  // Ensure probWith is not greater than probWithout
  const effectiveProbWith = Math.min(probWith, probWithout);

  // 1. Calculate annual avoided expected damage cost benefit
  const annualExpectedDamageWithout = damageWithout * probWithout;
  const annualExpectedDamageWith = effectiveDamageWith * effectiveProbWith;
  const annualAvoidedDamage = annualExpectedDamageWithout - annualExpectedDamageWith;

  // 2. Calculate annual insurance savings
  const annualInsuranceSavings = currentPremium - effectivePostPremium;

  // 3. Calculate annual property value increase attributed to mitigation
  // This reflects how the investment itself yields value through increased desirability/safety
  const annualPropertyValueIncrease = initialCost * (propValueApprecAnnual / 100);

  // 4. Calculate total mitigation investment over the time horizon
  const totalMitigationCost = initialCost + (annualMaintCost * horizon);

  // 5. Calculate total benefits over the time horizon
  const totalAvoidedDamageBenefit = annualAvoidedDamage * horizon;
  const totalInsuranceSavings = annualInsuranceSavings * horizon;
  const totalPropertyValueIncreaseBenefit = annualPropertyValueIncrease * horizon;

  // 6. Calculate Net Financial Benefit
  const totalBenefits = totalAvoidedDamageBenefit + totalInsuranceSavings + totalPropertyValueIncreaseBenefit;
  const netFinancialBenefit = totalBenefits - totalMitigationCost;

  // 7. Calculate Return on Investment (ROI)
  let roiPercentage = 0;
  if (totalMitigationCost > 0) {
    roiPercentage = (netFinancialBenefit / totalMitigationCost) * 100;
  } else if (netFinancialBenefit > 0) {
    roiPercentage = Infinity; // Infinite ROI if cost is zero and benefits exist
  }

  // 8. Calculate Payback Period
  let paybackPeriod = Infinity;
  const annualNetBenefitBeforePayback = annualAvoidedDamage + annualInsuranceSavings + annualPropertyValueIncrease - annualMaintCost;

  if (annualNetBenefitBeforePayback > 0) {
    paybackPeriod = initialCost / annualNetBenefitBeforePayback;
    // Cap payback period at a reasonable max if it's too long, or at time horizon
    paybackPeriod = Math.min(paybackPeriod, horizon * 2); // Cap at 2x horizon to avoid overly large numbers
  } else if (annualNetBenefitBeforePayback <= 0 && initialCost > 0) {
    paybackPeriod = Infinity; // Never pays back if annual benefits don't cover annual costs
  } else if (initialCost <= 0 && annualNetBenefitBeforePayback >= 0) {
    paybackPeriod = 0; // Immediate payback if initial cost is zero and benefits exist
  }

  // Ensure payback period is not negative
  paybackPeriod = Math.max(0, paybackPeriod);

  return {
    totalMitigationCost: totalMitigationCost,
    totalAvoidedDamageBenefit: totalAvoidedDamageBenefit,
    totalInsuranceSavings: totalInsuranceSavings,
    totalPropertyValueIncreaseBenefit: totalPropertyValueIncreaseBenefit,
    netFinancialBenefit: netFinancialBenefit,
    roiPercentage: roiPercentage,
    paybackPeriod: paybackPeriod
  };
}
