const volume = parseFloat(inputs.storageTB) || 0;
const egress = parseFloat(inputs.egressGB) || 0;
const access = inputs.accessFrequency;

// Simplified Pricing Models (approximate averages)
// AWS S3
let awsStorageRate = 0.023; // Standard
if (access.includes('Cool')) awsStorageRate = 0.0125; // IA
if (access.includes('Archive')) awsStorageRate = 0.004; // Glacier
const awsStorageCost = volume * 1024 * awsStorageRate;
const awsEgressCost = egress * 0.09;

// Azure Blob
let azureStorageRate = 0.018; // Hot
if (access.includes('Cool')) azureStorageRate = 0.01; // Cool
if (access.includes('Archive')) azureStorageRate = 0.00099; // Archive
const azureStorageCost = volume * 1024 * azureStorageRate;
const azureEgressCost = egress * 0.087;

// GCP Storage
let gcpStorageRate = 0.020; // Standard
if (access.includes('Cool')) gcpStorageRate = 0.01; // Nearline
if (access.includes('Archive')) gcpStorageRate = 0.0012; // Archive
const gcpStorageCost = volume * 1024 * gcpStorageRate;
const gcpEgressCost = egress * 0.12;

return {
    awsCost: awsStorageCost + awsEgressCost,
    azureCost: azureStorageCost + azureEgressCost,
    gcpCost: gcpStorageCost + gcpEgressCost
};
