function calculate({ propertyValue, currentFloodDamageCost, projectedSeaLevelRise, projectedFloodFrequencyIncrease, mitigationStrategyCost, damageReductionPercentage, insurancePremiumReduction, propertyValueUplift, discountRate, timeHorizon }) {

  // Convert percentages to decimals
  const drp = damageReductionPercentage / 100;
  const pvu = propertyValueUplift / 100;
  const dr = discountRate / 100;
  const pffi = projectedFloodFrequencyIncrease / 100;

  // Handle edge cases for mitigation cost
  if (mitigationStrategyCost <= 0) {
    return {
      totalAvoidedDamage: 0,
      totalInsuranceSavings: 0,
      netPresentValue: 0,
      roiPercentage: Infinity, // Indicates infinite ROI as cost is zero
      paybackPeriod: 0
    };
  }

  // Calculate annual escalation rate for baseline damage due to SLR and frequency increase
  // Assumption: SLR adds a certain percentage risk per inch per year, and flood frequency increase is linear over time horizon.
  // This is a simplified model. Every inch of SLR is assumed to increase baseline damage potential by 0.5% annually.
  const annualSLRDamageIncreaseFactor = (projectedSeaLevelRise / timeHorizon) * 0.005; // 0.5% per inch per year
  const annualFrequencyDamageIncreaseFactor = pffi / timeHorizon; // Linear distribution of total frequency increase
  const totalAnnualDamageEscalationRate = annualSLRDamageIncreaseFactor + annualFrequencyDamageIncreaseFactor;

  let totalAvoidedDamageUndiscounted = 0;
  let totalInsuranceSavingsUndiscounted = 0;
  let totalDiscountedBenefits = 0;

  let cumulativeDiscountedBenefits = [];

  for (let year = 1; year <= timeHorizon; year++) {
    // Calculate escalated baseline damage for the current year
    const escalatedBaselineDamage = currentFloodDamageCost * Math.pow(1 + totalAnnualDamageEscalationRate, year);

    // Calculate avoided damage for the current year
    const annualAvoidedDamage = escalatedBaselineDamage * drp;
    totalAvoidedDamageUndiscounted += annualAvoidedDamage;

    // Insurance savings are assumed constant annually
    const annualInsuranceSavings = insurancePremiumReduction;
    totalInsuranceSavingsUndiscounted += annualInsuranceSavings;

    // Calculate discounted benefits for the current year
    const annualBenefit = annualAvoidedDamage + annualInsuranceSavings;
    const discountedAnnualBenefit = annualBenefit / Math.pow(1 + dr, year);
    totalDiscountedBenefits += discountedAnnualBenefit;
    cumulativeDiscountedBenefits.push(totalDiscountedBenefits);
  }

  // Add property value uplift (one-time benefit at the end of the time horizon)
  const finalPropertyValueUplift = propertyValue * pvu;
  const discountedFinalPropertyValueUplift = finalPropertyValueUplift / Math.pow(1 + dr, timeHorizon);
  totalDiscountedBenefits += discountedFinalPropertyValueUplift;

  // Calculate Net Present Value (NPV)
  const netPresentValue = totalDiscountedBenefits - mitigationStrategyCost;

  // Calculate simple ROI (undiscounted total benefits)
  const totalUndiscountedBenefits = totalAvoidedDamageUndiscounted + totalInsuranceSavingsUndiscounted + finalPropertyValueUplift;
  const roiPercentage = ((totalUndiscountedBenefits - mitigationStrategyCost) / mitigationStrategyCost) * 100;

  // Calculate Payback Period
  let paybackPeriod = Infinity;
  let currentCumulativeBenefits = 0;
  for (let year = 0; year < timeHorizon; year++) {
    // Recalculate benefits for payback period with discounting
    const escalatedBaselineDamage = currentFloodDamageCost * Math.pow(1 + totalAnnualDamageEscalationRate, year + 1);
    const annualAvoidedDamage = escalatedBaselineDamage * drp;
    const annualBenefit = annualAvoidedDamage + insurancePremiumReduction;
    const discountedAnnualBenefit = annualBenefit / Math.pow(1 + dr, year + 1);

    currentCumulativeBenefits += discountedAnnualBenefit;

    if (currentCumulativeBenefits >= mitigationStrategyCost) {
      paybackPeriod = year + 1; // Current year is the payback year
      // Linear interpolation for fractional year
      if (year > 0) {
        const previousCumulativeBenefits = currentCumulativeBenefits - discountedAnnualBenefit;
        paybackPeriod = year + (mitigationStrategyCost - previousCumulativeBenefits) / discountedAnnualBenefit;
      }
      break;
    }
  }

  // Cap ROI if it's excessively high (e.g., if cost is very low compared to benefits)
  const MAX_ROI = 100000; // Cap at 100,000% to prevent misleadingly large numbers
  const finalRoiPercentage = Math.min(roiPercentage, MAX_ROI);

  return {
    totalAvoidedDamage: totalAvoidedDamageUndiscounted,
    totalInsuranceSavings: totalInsuranceSavingsUndiscounted,
    netPresentValue: netPresentValue,
    roiPercentage: finalRoiPercentage,
    paybackPeriod: paybackPeriod > timeHorizon ? 'Never within horizon' : paybackPeriod
  };
}
