function calculateROI(inputs) {
    const { 
        initialConstructionCost, 
        publicFundingPercentage, 
        annualOperatingCost, 
        projectLifespanYears, 
        annualTourismVisits, 
        avgTourismSpendPerVisit, 
        annualEventRevenue, 
        localMembershipRevenue, 
        localPopulationServed, 
        communityHealthBenefitValuePerCapita
    } = inputs;

    const discountRate = 0.05; // Assumed annual discount rate of 5% for NPV calculation

    // 1. Calculate Net Initial Investment
    const netInitialInvestment = initialConstructionCost * (1 - publicFundingPercentage / 100);

    // 2. Calculate Total Annual Revenue
    const totalAnnualRevenue = 
        (annualTourismVisits * avgTourismSpendPerVisit) +
        annualEventRevenue +
        localMembershipRevenue;

    // 3. Calculate Annual Net Cash Flow (Financial)
    const annualNetCashFlow = totalAnnualRevenue - annualOperatingCost;

    // 4. Calculate Total Annual Community Benefit Value
    const totalAnnualCommunityBenefitValue = localPopulationServed * communityHealthBenefitValuePerCapita;

    // 5. Calculate Net Present Value (NPV) - Financial
    let netPresentValue = -netInitialInvestment;
    if (projectLifespanYears > 0) {
        for (let t = 1; t <= projectLifespanYears; t++) {
            netPresentValue += annualNetCashFlow / Math.pow(1 + discountRate, t);
        }
    }

    // 6. Calculate Present Value of Community Benefits
    let pvCommunityBenefits = 0;
    if (projectLifespanYears > 0) {
        for (let t = 1; t <= projectLifespanYears; t++) {
            pvCommunityBenefits += totalAnnualCommunityBenefitValue / Math.pow(1 + discountRate, t);
        }
    }

    // 7. Calculate Financial Payback Period
    let paybackPeriodYears = 'Never';
    if (annualNetCashFlow > 0 && netInitialInvestment > 0) {
        const rawPayback = netInitialInvestment / annualNetCashFlow;
        paybackPeriodYears = Math.min(rawPayback, projectLifespanYears); // Cap at project lifespan
        if (paybackPeriodYears < 0) paybackPeriodYears = 'Never'; // Ensure it's not negative for positive cash flow scenario
    } else if (netInitialInvestment <= 0) {
        paybackPeriodYears = 0; // If no initial investment, payback is immediate
    }

    // 8. Calculate Total Project Economic Value
    const totalProjectEconomicValue = netPresentValue + pvCommunityBenefits;

    // 9. Calculate Overall Project ROI Percentage
    let roiPercentage = 0;
    if (netInitialInvestment > 0) {
        roiPercentage = ((totalProjectEconomicValue - netInitialInvestment) / netInitialInvestment) * 100;
    } else if (netInitialInvestment <= 0 && totalProjectEconomicValue > 0) { 
        // If initial investment is zero or negative (e.g., fully funded by grants, or negative for tax benefits), 
        // and the project still generates positive total economic value, it's considered an infinite ROI. 
        roiPercentage = Infinity; 
    } else if (netInitialInvestment <= 0 && totalProjectEconomicValue <= 0) {
        roiPercentage = 0; // No investment, no value
    }

    return {
        netInitialInvestment,
        totalAnnualRevenue,
        annualNetCashFlow,
        totalAnnualCommunityBenefitValue,
        netPresentValue,
        paybackPeriodYears: typeof paybackPeriodYears === 'number' ? parseFloat(paybackPeriodYears.toFixed(1)) : paybackPeriodYears,
        totalProjectEconomicValue,
        roiPercentage
    };
}
