const CORE_TEMP_NORMAL = 37.0;
const CORE_TEMP_HYPOTHERMIA_MODERATE = 35.0;
const BODY_SURFACE_AREA_AVG = 1.8; // m^2 (average adult)
const SPECIFIC_HEAT_HUMAN = 3470; // J/kg°C (average human specific heat capacity)
const BODY_MASS_AVG = 70; // kg (average adult)

// 1. Calculate Wind Chill Temperature (WCT)
let WCT;
if (ambientTemperatureC > 10 || windSpeedKmh < 3) { // Simplified threshold for when wind chill isn't significant
    WCT = ambientTemperatureC;
} else {
    // Canadian Wind Chill Index formula (modified for km/h and Celsius)
    WCT = 13.12 + (0.6215 * ambientTemperatureC) - (11.37 * Math.pow(windSpeedKmh, 0.16)) + (0.3965 * ambientTemperatureC * Math.pow(windSpeedKmh, 0.16));
}
WCT = Math.min(WCT, ambientTemperatureC); // WCT cannot be warmer than ambient temperature

// 2. Calculate Effective Insulation Factor (Clothing + Body Fat)
// CLO values: 0.0 (naked), 1.0 (typical business suit), 2.0-3.0 (heavy winter clothing)
// A common conversion is 1 CLO = 0.155 m²·°C/W (thermal resistance)
// We want a factor that reduces heat transfer as insulation increases.
const R_clothing = clothingInsulationCLO * 0.155; // m²·°C/W

// Body fat provides inherent insulation. Higher body fat means less heat loss.
let bodyFatInsulationEffect = 1.0;
// Assume 15-20% body fat is 'average' (factor 1).
// Adjust factor based on deviation from average, inversely proportional to fat.
if (bodyFatPercent < 15) {
    bodyFatInsulationEffect = 1.0 + (15 - bodyFatPercent) * 0.015; // +1.5% vulnerability for each % below 15
} else if (bodyFatPercent > 20) {
    bodyFatInsulationEffect = 1.0 - (bodyFatPercent - 20) * 0.01; // -1% vulnerability for each % above 20
}
bodyFatInsulationEffect = Math.max(0.7, Math.min(1.3, bodyFatInsulationEffect)); // Cap adjustment range

// Effective thermal resistance. A baseline naked resistance + clothing resistance.
// R_skin_air is complex. Let's use an approximate overall resistance.
// Naked R_avg ~ 0.1 m²·°C/W (surface resistance)
const R_total_effective = 0.1 + R_clothing; // Simplified sum of resistances

// Convert total resistance to a heat transfer coefficient (U = 1/R)
const U_effective = 1 / R_total_effective; // W/m²·°C

// 3. Calculate Heat Production (Metabolic Rate)
// 1 MET = 58.2 W/m²
const heatProductionRate = activityMET * 58.2 * BODY_SURFACE_AREA_AVG; // Watts

// 4. Calculate Heat Loss Rate
// Heat loss is driven by the temperature difference between core/skin and WCT.
const SKIN_TEMP_AVG = 33.0; // °C (approximate average skin temperature)
const tempDifference = SKIN_TEMP_AVG - WCT;

// Base heat loss rate before adjustments (W)
let netHeatLossFromEnvironment = U_effective * BODY_SURFACE_AREA_AVG * tempDifference;

// Age and health vulnerability factors increase susceptibility to heat loss or reduce heat production/regulation.
let ageEffect = 1.0;
if (ageYears >= 70) {
    ageEffect = 1.3; // 30% increased vulnerability
} else if (ageYears >= 50) {
    ageEffect = 1.15; // 15% increased vulnerability
} else if (ageYears < 10) {
    ageEffect = 1.2; // Children are also more vulnerable
}

// Apply all vulnerability factors to environmental heat loss
netHeatLossFromEnvironment *= ageEffect * healthVulnerabilityFactor * bodyFatInsulationEffect; // Note: bodyFatInsulationEffect is a multiplier here

// Net heat exchange (Watts): Heat production - Heat loss
// If positive, body is gaining/maintaining heat. If negative, body is losing heat.
let netHeatExchangeRate = heatProductionRate - netHeatLossFromEnvironment;

// If the body is gaining heat, risk is low. We are interested in net heat *loss*.
let actualNetHeatLossRate = Math.max(0, -netHeatExchangeRate); // Only consider loss

// 5. Calculate Hypothermia Risk Index
// Risk index is cumulative, so it depends on duration.
// Scale this to a 0-100 score. A rough estimate: 100W net loss for 1 hour could be moderate risk.
const BASE_RISK_THRESHOLD = 50; // J/W (arbitrary scaling constant for risk index)
let hypothermiaRiskIndex = (actualNetHeatLossRate * exposureDurationHours * 3600) / (BASE_RISK_THRESHOLD * 1000); // Scale to produce a score

hypothermiaRiskIndex = Math.min(100, Math.max(0, hypothermiaRiskIndex)); // Cap between 0 and 100

// 6. Determine Risk Category
let riskCategory = "Unknown";
if (hypothermiaRiskIndex < 5) {
    riskCategory = "Minimal Risk";
} else if (hypothermiaRiskIndex < 20) {
    riskCategory = "Low Risk";
} else if (hypothermiaRiskIndex < 45) {
    riskCategory = "Moderate Risk";
} else if (hypothermiaRiskIndex < 75) {
    riskCategory = "High Risk";
} else {
    riskCategory = "Extreme Risk";
}

// 7. Estimate Time to Moderate Hypothermia (rough estimate)
let timeToHypothermiaHours = Infinity;
if (actualNetHeatLossRate > 0) {
    const energyToDropCoreTemp = BODY_MASS_AVG * SPECIFIC_HEAT_HUMAN * (CORE_TEMP_NORMAL - CORE_TEMP_HYPOTHERMIA_MODERATE); // Joules
    timeToHypothermiaHours = energyToDropCoreTemp / (actualNetHeatLossRate * 3600); // Convert Watts to J/hour
}

// Cap time to hypothermia for very low risk scenarios
if (timeToHypothermiaHours === Infinity) {
    timeToHypothermiaHours = 999; // Represents 'Very Long' or 'No Risk'
} else if (timeToHypothermiaHours > 48) {
    timeToHypothermiaHours = 48; // If more than 2 days, cap it as 'Very Long (>48h)'
} else if (timeToHypothermiaHours < 0.25) {
    timeToHypothermiaHours = 0.25; // Minimum time of 15 minutes
}

return {
    hypothermiaRiskIndex: parseFloat(hypothermiaRiskIndex.toFixed(1)),
    riskCategory: riskCategory,
    timeToHypothermia: parseFloat(timeToHypothermiaHours.toFixed(2))
};
