function calculateColdExposureRisk(inputs) {
  const { ambientTemperature, windSpeed, activityLevel, clothingInsulation, exposureDuration, bodyMass, height, age, gender } = inputs;

  // --- Constants and Conversions ---
  const KELVIN_OFFSET = 273.15;
  const SPECIFIC_HEAT_BODY_J_PER_KG_C = 3470; // J/kg/C
  const MET_TO_W_PER_SQM = 58.2; // 1 MET = 58.2 W/m²
  const CLO_TO_R_SI = 0.155; // 1 CLO = 0.155 m²K/W
  const R_AIR_STILL_SI = 0.1; // Still air thermal resistance (m²K/W)
  const T_CORE_AVG = 37; // Reference core body temperature in °C
  const T_SKIN_AVG = 30; // Reference average skin temperature in °C

  // Input Validation and Defaults
  if (bodyMass <= 0 || height <= 0 || exposureDuration <= 0) {
    return { 
      frostbiteRiskLevel: 'Error', 
      hypothermiaRiskLevel: 'Error', 
      timeToFrostbite: NaN, 
      estimatedHeatLossRate: NaN 
    };
  }

  // --- 1. Wind Chill Temperature (WCT) Calculation (NWS/MSC Formula) ---
  // Valid for T_air <= 10°C and V >= 4.8 km/h
  let windChillTemperature = ambientTemperature;
  const windSpeedKmH = Math.max(0, windSpeed); // Ensure non-negative

  if (ambientTemperature <= 10 && windSpeedKmH >= 4.8) {
    windChillTemperature = 13.12 + (0.6215 * ambientTemperature) - (11.37 * Math.pow(windSpeedKmH, 0.16)) + (0.3965 * ambientTemperature * Math.pow(windSpeedKmH, 0.16));
  } else if (ambientTemperature <= -20 && windSpeedKmH < 4.8) {
      // For very cold temps with low wind, ambient temp still poses a risk
      // But without significant wind, wind chill formula isn't applicable
      windChillTemperature = ambientTemperature;
  }

  // Cap WCT for extremely high wind speeds to prevent unrealistically low values
  windChillTemperature = Math.max(windChillTemperature, -80); 

  // --- 2. Frostbite Risk & Time to Frostbite (TTF) ---
  let timeToFrostbiteMinutes = Infinity; // minutes
  let frostbiteRiskLevel = 'Low';

  if (windChillTemperature <= -15) {
    // Based on NWS frostbite charts for exposed skin
    if (windChillTemperature <= -48) timeToFrostbiteMinutes = 2;
    else if (windChillTemperature <= -40) timeToFrostbiteMinutes = 5;
    else if (windChillTemperature <= -34) timeToFrostbiteMinutes = 10;
    else if (windChillTemperature <= -28) timeToFrostbiteMinutes = 30;
    else if (windChillTemperature <= -20) timeToFrostbiteMinutes = 60;
    else if (windChillTemperature <= -15) timeToFrostbiteMinutes = 120;

    if (timeToFrostbiteMinutes <= exposureDuration * 60) {
      if (timeToFrostbiteMinutes <= 10) frostbiteRiskLevel = 'Extreme';
      else if (timeToFrostbiteMinutes <= 30) frostbiteRiskLevel = 'High';
      else if (timeToFrostbiteMinutes <= 60) frostbiteRiskLevel = 'Moderate';
      else frostbiteRiskLevel = 'Low'; // Still a risk, but not immediate
    } else {
      frostbiteRiskLevel = 'Low';
    }
  }

  // --- 3. Body Surface Area (BSA) Calculation (DuBois Formula) ---
  const bsa = 0.007184 * Math.pow(bodyMass, 0.425) * Math.pow(height, 0.725);

  // --- 4. Metabolic Heat Production (M) ---
  const metabolicRateWatts = activityLevel * bsa * MET_TO_W_PER_SQM;

  // --- 5. Clothing Thermal Resistance ---
  const rClothingSi = clothingInsulation * CLO_TO_R_SI;

  // --- 6. Effective Air Thermal Resistance (affected by wind) ---
  const windSpeedMps = windSpeedKmH / 3.6;
  const rAirEffectiveSi = R_AIR_STILL_SI / (1 + 0.5 * Math.sqrt(windSpeedMps)); // Simplified wind effect

  // --- 7. Total Heat Loss Rate Estimation (for hypothermia) ---
  // Using T_CORE_AVG as the driving temperature for heat loss to the environment
  // We use a blend of ambient and WCT for the effective environmental temperature
  let effectiveEnvironmentalTemperature = ambientTemperature;
  if (windChillTemperature < ambientTemperature && ambientTemperature < 15) { // If wind chill is significantly colder than ambient
    effectiveEnvironmentalTemperature = 0.7 * windChillTemperature + 0.3 * ambientTemperature; // Blend for body's overall perception
  }
  effectiveEnvironmentalTemperature = Math.max(effectiveEnvironmentalTemperature, -50);

  const totalThermalResistanceSi = rClothingSi + rAirEffectiveSi;

  // Heat loss from core to environment through total resistance
  // Q_loss = A * (T_core - T_env_effective) / R_total
  const heatLossRateWatts = bsa * (T_CORE_AVG - effectiveEnvironmentalTemperature) / totalThermalResistanceSi;

  // --- 8. Net Heat Balance and Core Body Temperature Drop ---
  const netHeatGainLossWatts = metabolicRateWatts - heatLossRateWatts;
  const estimatedHeatLossRate = -netHeatGainLossWatts; // Output as positive for net loss, negative for net gain

  // If netHeatGainLossWatts is negative, there's a net heat loss.
  const heatLostJoules = -netHeatGainLossWatts * exposureDuration * 3600; // Total Joules lost over duration

  let deltaTCoreC = 0;
  if (heatLostJoules > 0) { // Only calculate drop if there's actual heat lost
    deltaTCoreC = heatLostJoules / (bodyMass * SPECIFIC_HEAT_BODY_J_PER_KG_C);
  }

  // --- 9. Age and Gender Vulnerability Factors ---
  let vulnerabilityFactor = 1.0;
  if (age < 12) vulnerabilityFactor *= 1.3; // Children more vulnerable
  if (age >= 65) vulnerabilityFactor *= 1.5; // Elderly more vulnerable
  if (gender.toLowerCase() === 'female') vulnerabilityFactor *= 1.1; // Females generally slightly more susceptible

  deltaTCoreC *= vulnerabilityFactor;

  // --- 10. Hypothermia Risk Level ---
  let hypothermiaRiskLevel = 'Low';
  const finalCoreTemp = T_CORE_AVG - deltaTCoreC; // Predicted core temperature

  if (deltaTCoreC >= 3) { // Core temp <= 34°C (Moderate hypothermia begins around 34°C)
    hypothermiaRiskLevel = 'Extreme';
  } else if (deltaTCoreC >= 2) { // Core temp <= 35°C (Mild hypothermia begins around 35°C)
    hypothermiaRiskLevel = 'High';
  } else if (deltaTCoreC >= 1) { // Core temp <= 36°C
    hypothermiaRiskLevel = 'Moderate';
  } else {
    hypothermiaRiskLevel = 'Low';
  }

  // Adjust risk if conditions are very severe initially, regardless of precise delta T calculation over time
  if (windChillTemperature <= -35 && hypothermiaRiskLevel === 'Low') {
      hypothermiaRiskLevel = 'Moderate';
  }

  return {
    frostbiteRiskLevel: frostbiteRiskLevel,
    hypothermiaRiskLevel: hypothermiaRiskLevel,
    timeToFrostbite: Math.min(Math.round(timeToFrostbiteMinutes), 9999), // Cap for display
    estimatedHeatLossRate: Math.round(estimatedHeatLossRate)
  };
}
