function calculateHeatLoss(inputs) {
  const { 
    outdoorTempF, indoorTempF, conditionedFloorAreaSqFt, 
    avgWallRValue, atticRValue, totalWindowAreaSqFt, 
    avgWindowUValue, airChangeRateACH, furnaceEfficiencyAFUE, 
    heatingFuelCostPerTherm, durationHours 
  } = inputs;

  // Input validation and defaults
  const validate = (value, min = 0.01, max = Infinity, fallback) => {
    if (typeof value !== 'number' || isNaN(value) || value < min || value > max) {
      return fallback !== undefined ? fallback : min; 
    }
    return value;
  };

  const safeOutdoorTemp = outdoorTempF;
  const safeIndoorTemp = indoorTempF;
  const safeFloorArea = validate(conditionedFloorAreaSqFt, 1, 100000, 1);
  const safeWallR = validate(avgWallRValue, 1, 100, 1);
  const safeAtticR = validate(atticRValue, 1, 200, 1);
  const safeWindowArea = validate(totalWindowAreaSqFt, 0, safeFloorArea * 0.5, 0); // Window area cannot exceed 50% of floor area
  const safeWindowU = validate(avgWindowUValue, 0.01, 2, 0.01);
  const safeACH = validate(airChangeRateACH, 0.01, 5, 0.01);
  const safeFurnaceEfficiency = validate(furnaceEfficiencyAFUE, 1, 100, 80);
  const safeFuelCost = validate(heatingFuelCostPerTherm, 0.01, 10, 0.01);
  const safeDuration = validate(durationHours, 1, 720, 1);

  const DELTA_T = Math.max(0, safeIndoorTemp - safeOutdoorTemp);
  if (DELTA_T === 0) {
    return {
      totalHeatLossBTU: 0,
      hourlyHeatLossBTU: 0,
      estimatedEnergyCost: 0,
      heatLossPercentWalls: 0,
      heatLossPercentAttic: 0,
      heatLossPercentWindows: 0,
      heatLossPercentAir: 0
    };
  }

  // --- Assumptions for Calculations ---
  // 1. Ceiling Height: 8 feet for conditioned volume.
  const CEILING_HEIGHT = 8; // feet
  // 2. Wall Area: Derived from floor area assuming a square footprint.
  //    Perimeter = 4 * sqrt(FloorArea), Wall Area = Perimeter * CeilingHeight
  const WALL_AREA_SQ_FT = 4 * Math.sqrt(safeFloorArea) * CEILING_HEIGHT;
  // 3. Attic Area: Assumed to be equal to conditioned floor area (for a single-story home).
  const ATTIC_AREA_SQ_FT = safeFloorArea;
  // 4. Specific Heat of Air: Approx. 0.018 BTU/ft³·°F (at standard conditions).
  const SPECIFIC_HEAT_OF_AIR = 0.018; // BTU/ft³·°F
  // 5. Therm to BTU conversion: 1 therm = 100,000 BTU
  const BTU_PER_THERM = 100000;

  // --- Heat Loss Calculations (BTU/hr) ---

  // 1. Heat Loss through Walls (Conduction)
  // Q = (Area / R-value) * Delta_T
  const heatLossWalls = (WALL_AREA_SQ_FT / safeWallR) * DELTA_T;

  // 2. Heat Loss through Attic/Roof (Conduction)
  // Q = (Area / R-value) * Delta_T
  const heatLossAttic = (ATTIC_AREA_SQ_FT / safeAtticR) * DELTA_T;

  // 3. Heat Loss through Windows (Conduction/Convection, U-value already accounts for this)
  // Q = Area * U-value * Delta_T
  const heatLossWindows = safeWindowArea * safeWindowU * DELTA_T;

  // 4. Heat Loss due to Air Infiltration (Convection)
  // Volume = Floor Area * Ceiling Height
  const conditionedVolume = safeFloorArea * CEILING_HEIGHT; // ft³
  // Q = Volume * ACH * SpecificHeatOfAir * Delta_T
  const heatLossAir = conditionedVolume * safeACH * SPECIFIC_HEAT_OF_AIR * DELTA_T;

  // --- Total Hourly Heat Loss ---
  const hourlyHeatLossBTU = heatLossWalls + heatLossAttic + heatLossWindows + heatLossAir;

  // --- Total Heat Loss over Duration ---
  const totalHeatLossBTU = hourlyHeatLossBTU * safeDuration;

  // --- Energy Required and Cost ---
  const furnaceEfficiencyFactor = safeFurnaceEfficiency / 100;
  const energyRequiredBTU = totalHeatLossBTU / furnaceEfficiencyFactor;
  const energyRequiredTherms = energyRequiredBTU / BTU_PER_THERM;
  const estimatedEnergyCost = energyRequiredTherms * safeFuelCost;

  // --- Percentage Breakdown ---
  const totalLossComponents = heatLossWalls + heatLossAttic + heatLossWindows + heatLossAir; // Use hourly for percentages
  const heatLossPercentWalls = (heatLossWalls / totalLossComponents) * 100;
  const heatLossPercentAttic = (heatLossAttic / totalLossComponents) * 100;
  const heatLossPercentWindows = (heatLossWindows / totalLossComponents) * 100;
  const heatLossPercentAir = (heatLossAir / totalLossComponents) * 100;

  return {
    totalHeatLossBTU: totalHeatLossBTU,
    hourlyHeatLossBTU: hourlyHeatLossBTU,
    estimatedEnergyCost: estimatedEnergyCost,
    heatLossPercentWalls: heatLossPercentWalls,
    heatLossPercentAttic: heatLossPercentAttic,
    heatLossPercentWindows: heatLossPercentWindows,
    heatLossPercentAir: heatLossPercentAir
  };
}
