function calculateColdWeatherRisk(inputs) {
  const tempC = parseFloat(inputs.ambientTempC);
  const windSpeedKmh = parseFloat(inputs.windSpeedKmh);
  const exposureDurationHours = parseFloat(inputs.exposureDurationHours);
  const activityLevel = inputs.activityLevel;
  const protectiveGear = inputs.protectiveGear;
  const individualSusceptibility = inputs.individualSusceptibility;
  const hydrationStatus = inputs.hydrationStatus;

  // Validate inputs
  if (isNaN(tempC) || isNaN(windSpeedKmh) || isNaN(exposureDurationHours)) {
    return { error: 'Please enter valid numbers for temperature, wind speed, and exposure duration.' };
  }
  if (exposureDurationHours <= 0 || windSpeedKmh < 0) {
      return { error: 'Exposure duration must be greater than 0, and wind speed cannot be negative.' };
  }

  // 1. Calculate Wind Chill Temperature (WCT) using the Environment Canada/US NWS model
  // Formula is valid for temperatures <= 10°C and wind speeds >= 5 km/h.
  // If temp is above 10C, wind chill is generally not a factor for cold injury, so WCT = tempC.
  // If wind speed is very low, WCT is approximately tempC.
  let windChillC;
  if (tempC > 10) {
    windChillC = tempC; // Wind chill not a factor for cold injury above 10C
  } else if (windSpeedKmh < 4.8) { // Below ~5 km/h, wind chill effect is minimal for formula
    windChillC = tempC;
  } else {
    windChillC = 13.12 + 0.6215 * tempC - 11.37 * Math.pow(windSpeedKmh, 0.16) + 0.3965 * tempC * Math.pow(windSpeedKmh, 0.16);
  }
  // Cap wind chill at ambient temp (it should never feel warmer due to wind)
  windChillC = Math.min(tempC, windChillC);

  // 2. Initialize Overall Risk Score (ORS) based on Wind Chill and Duration
  let overallInjuryRiskScore = 0;

  // Base risk increases significantly as WCT drops below -5C
  if (windChillC < -5) {
    overallInjuryRiskScore += Math.max(0, (-windChillC - 5) * 1.5); // Initial increase for every degree below -5C
  }
  // Duration factor - longer exposure means higher risk
  overallInjuryRiskScore += exposureDurationHours * 7; // Add 7 points per hour of exposure

  // 3. Adjust ORS based on activity level
  switch (activityLevel) {
    case 'sedentary':
      overallInjuryRiskScore += 15; // Significant increase
      break;
    case 'light':
      overallInjuryRiskScore += 5;
      break;
    case 'moderate':
      overallInjuryRiskScore -= 10; // Moderate reduction
      break;
    case 'strenuous':
      overallInjuryRiskScore -= 20; // Significant reduction (more heat generation)
      break;
  }

  // 4. Adjust ORS based on protective gear
  switch (protectiveGear) {
    case 'inadequate':
      overallInjuryRiskScore += 30; // High increase
      break;
    case 'basic':
      overallInjuryRiskScore += 15;
      break;
    case 'adequate':
      overallInjuryRiskScore -= 10;
      break;
    case 'excellent':
      overallInjuryRiskScore -= 25; // High reduction
      break;
  }

  // 5. Adjust ORS based on individual susceptibility
  switch (individualSusceptibility) {
    case 'low':
      overallInjuryRiskScore -= 5;
      break;
    case 'moderate':
      overallInjuryRiskScore += 5;
      break;
    case 'high':
      overallInjuryRiskScore += 20; // Significant increase
      break;
  }

  // 6. Adjust ORS based on hydration status
  switch (hydrationStatus) {
    case 'optimal':
      overallInjuryRiskScore -= 5;
      break;
    case 'suboptimal':
      overallInjuryRiskScore += 10;
      break;
    case 'poor':
      overallInjuryRiskScore += 25; // Significant increase
      break;
  }

  // Cap ORS between 0 and 100
  overallInjuryRiskScore = Math.max(0, Math.min(100, overallInjuryRiskScore));

  // 7. Determine Frostbite Risk Level
  let frostbiteRisk = 'None';
  if (windChillC <= -10 && exposureDurationHours >= 0.25) frostbiteRisk = 'Low';
  if (windChillC <= -15 && exposureDurationHours >= 0.1) frostbiteRisk = 'Moderate';
  if (windChillC <= -20) frostbiteRisk = 'Moderate';
  if (windChillC <= -25 && exposureDurationHours >= 0.1) frostbiteRisk = 'High';
  if (windChillC <= -30) frostbiteRisk = 'High';
  if (windChillC <= -35 && exposureDurationHours >= 0.05) frostbiteRisk = 'Severe';
  if (windChillC <= -40) frostbiteRisk = 'Severe';
  if (windChillC <= -45) frostbiteRisk = 'Extreme';

  // Further adjust frostbite risk based on overallInjuryRiskScore and protective gear
  if (protectiveGear === 'inadequate' && overallInjuryRiskScore > 40) {
      if (frostbiteRisk === 'None') frostbiteRisk = 'Low';
      else if (frostbiteRisk === 'Low') frostbiteRisk = 'Moderate';
      else if (frostbiteRisk === 'Moderate') frostbiteRisk = 'High';
      else if (frostbiteRisk === 'High') frostbiteRisk = 'Severe';
      else if (frostbiteRisk === 'Severe') frostbiteRisk = 'Extreme';
  }
  if (protectiveGear === 'basic' && overallInjuryRiskScore > 60 && frostbiteRisk !== 'Extreme') {
      if (frostbiteRisk === 'None') frostbiteRisk = 'Low';
      else if (frostbiteRisk === 'Low') frostbiteRisk = 'Moderate';
      else if (frostbiteRisk === 'Moderate') frostbiteRisk = 'High';
  }
  if (protectiveGear === 'excellent' && overallInjuryRiskScore < 20 && frostbiteRisk !== 'None') {
    if (frostbiteRisk === 'Extreme') frostbiteRisk = 'Severe';
    else if (frostbiteRisk === 'Severe') frostbiteRisk = 'High';
    else if (frostbiteRisk === 'High') frostbiteRisk = 'Moderate';
    else if (frostbiteRisk === 'Moderate') frostbiteRisk = 'Low';
    else if (frostbiteRisk === 'Low') frostbiteRisk = 'None';
  }

  // 8. Determine Hypothermia Risk Level
  let hypothermiaRisk = 'None';
  if (overallInjuryRiskScore >= 20) hypothermiaRisk = 'Low';
  if (overallInjuryRiskScore >= 40) hypothermiaRisk = 'Moderate';
  if (overallInjuryRiskScore >= 60) hypothermiaRisk = 'High';
  if (overallInjuryRiskScore >= 80) hypothermiaRisk = 'Severe';

  // 9. Generate Mitigation Recommendations
  let mitigationRecommendations = '';
  if (overallInjuryRiskScore < 20) {
    mitigationRecommendations = 'Your overall cold injury risk is low. Continue to monitor conditions and stay hydrated.';
  } else if (overallInjuryRiskScore < 40) {
    mitigationRecommendations = 'Your overall cold injury risk is low to moderate. Ensure proper layering, stay active, and take warm breaks. Pay attention to early signs of cold exposure.';
  } else if (overallInjuryRiskScore < 60) {
    mitigationRecommendations = 'Your overall cold injury risk is moderate. Prioritize multi-layer, dry clothing, protect exposed skin, and limit continuous exposure. Consider bringing emergency supplies.';
  } else if (overallInjuryRiskScore < 80) {
    mitigationRecommendations = 'Your overall cold injury risk is high. Extreme caution is advised. Minimize time outdoors, seek immediate shelter, and wear maximum protective gear. Be vigilant for signs of frostbite or hypothermia.';
  } else {
    mitigationRecommendations = 'Your overall cold injury risk is severe. Avoid outdoor exposure if possible. If unavoidable, ensure comprehensive protection, constant monitoring, and immediate access to warm shelter and emergency services. This situation is life-threatening.';
  }

  // Add specific recommendations based on input choices if they increased risk
  if (protectiveGear === 'inadequate') mitigationRecommendations += ' *URGENT*: Improve protective gear immediately. Exposed skin is highly vulnerable.';
  if (hydrationStatus === 'poor') mitigationRecommendations += ' *IMPORTANT*: Rehydrate with warm fluids. Dehydration significantly increases cold injury risk.';
  if (activityLevel === 'sedentary') mitigationRecommendations += ' *ADVICE*: Increase activity levels or take frequent, warm breaks to generate body heat.';
  if (individualSusceptibility === 'high') mitigationRecommendations += ' *CAUTION*: Due to high personal susceptibility, be extra vigilant and prioritize safety measures.';

  return {
    windChillC: windChillC,
    frostbiteRisk: frostbiteRisk,
    hypothermiaRisk: hypothermiaRisk,
    overallInjuryRiskScore: Math.round(overallInjuryRiskScore),
    mitigationRecommendations: mitigationRecommendations
  };
}
