function calculateCollectiblesValue({ initialPurchasePrice, anticipatedHoldingPeriodYears, annualMarketTrendRate, assetSpecificAnnualRate, conditionFactor, rarityFactor, annualCostPercentage, inflationRate }) {
  // Input validation and default values
  initialPurchasePrice = Math.max(0, initialPurchasePrice || 0);
  anticipatedHoldingPeriodYears = Math.max(0, anticipatedHoldingPeriodYears || 0);
  annualMarketTrendRate = (annualMarketTrendRate || 0) / 100; // Convert to decimal
  assetSpecificAnnualRate = (assetSpecificAnnualRate || 0) / 100; // Convert to decimal
  conditionFactor = Math.min(Math.max(0.5, conditionFactor || 1.0), 2.0); // Clamp between 0.5 and 2.0
  rarityFactor = Math.min(Math.max(1.0, rarityFactor || 1.0), 3.0); // Clamp between 1.0 and 3.0
  annualCostPercentage = (annualCostPercentage || 0) / 100; // Convert to decimal
  inflationRate = (inflationRate || 0) / 100; // Convert to decimal

  if (initialPurchasePrice <= 0 || anticipatedHoldingPeriodYears === 0) {
    return {
      projectedFutureNominalValue: initialPurchasePrice,
      projectedFutureRealValue: initialPurchasePrice,
      totalNetAppreciation: 0,
    };
  }

  // Step 1: Calculate the blended annual appreciation rate
  // This combines the general market trend with the asset-specific historical rate.
  // We give more weight to the asset-specific rate if provided.
  let effectiveAppreciationRate;
  if (assetSpecificAnnualRate !== 0 && annualMarketTrendRate !== 0) {
      effectiveAppreciationRate = (assetSpecificAnnualRate * 0.7) + (annualMarketTrendRate * 0.3); // More weight to specific
  } else if (assetSpecificAnnualRate !== 0) {
      effectiveAppreciationRate = assetSpecificAnnualRate;
  } else {
      effectiveAppreciationRate = annualMarketTrendRate;
  }

  // Step 2: Apply condition and rarity factors to the base value
  let baseValueAdjusted = initialPurchasePrice * conditionFactor * rarityFactor;

  // Step 3: Project nominal future value before costs
  let projectedNominalValueBeforeCosts = baseValueAdjusted * Math.pow(1 + effectiveAppreciationRate, anticipatedHoldingPeriodYears);

  // Step 4: Calculate total accumulated annual costs based on initial value
  let totalAccumulatedCosts = initialPurchasePrice * annualCostPercentage * anticipatedHoldingPeriodYears;

  // Step 5: Calculate projected nominal value after costs
  let projectedFutureNominalValue = projectedNominalValueBeforeCosts - totalAccumulatedCosts;
  projectedFutureNominalValue = Math.max(0, projectedFutureNominalValue); // Ensure value doesn't drop below zero

  // Step 6: Calculate the real (inflation-adjusted) future value
  let projectedFutureRealValue = projectedFutureNominalValue / Math.pow(1 + inflationRate, anticipatedHoldingPeriodYears);
  projectedFutureRealValue = Math.max(0, projectedFutureRealValue); // Ensure value doesn't drop below zero

  // Step 7: Calculate total net appreciation (nominal)
  let totalNetAppreciation = projectedFutureNominalValue - initialPurchasePrice;

  return {
    projectedFutureNominalValue: projectedFutureNominalValue,
    projectedFutureRealValue: projectedFutureRealValue,
    totalNetAppreciation: totalNetAppreciation,
  };
}
