const principal = inputs.principalLoanAmount;
const annualRate = inputs.annualInterestRate / 100; // Convert percentage to decimal
const termYears = inputs.loanTermYears;
const annualIncome = inputs.projectedAnnualIncome;

// Convert to monthly
const monthlyRate = annualRate / 12;
const numPayments = termYears * 12;

let monthlyPayment = 0;
let totalInterestPaid = 0;
let totalPaid = 0;
let incomePaymentRatio = 0;
let affordabilityMessage = "";

if (principal > 0 && termYears > 0) {
    if (annualRate === 0) { // Handle 0 interest rate separately
        monthlyPayment = principal / numPayments;
    } else {
        // Standard loan amortization formula
        monthlyPayment = principal * (monthlyRate * Math.pow(1 + monthlyRate, numPayments)) / (Math.pow(1 + monthlyRate, numPayments) - 1);
    }
    totalPaid = monthlyPayment * numPayments;
    totalInterestPaid = totalPaid - principal;

    if (annualIncome > 0) {
        const monthlyIncome = annualIncome / 12;
        incomePaymentRatio = (monthlyPayment / monthlyIncome) * 100;

        if (incomePaymentRatio <= 8) {
            affordabilityMessage = "Excellent: This payment appears highly affordable given your projected income.";
        } else if (incomePaymentRatio <= 12) {
            affordabilityMessage = "Good: This payment is manageable, allowing for other financial goals. Budget carefully.";
        } else if (incomePaymentRatio <= 18) {
            affordabilityMessage = "Moderate: This payment might be a significant portion of your income. Strict budgeting will be crucial.";
        } else {
            affordabilityMessage = "High: This payment may be difficult to afford. Seriously explore strategies like increasing income, reducing loan principal, or investigating income-driven repayment options.";
        }
    } else {
        affordabilityMessage = "Cannot assess affordability without projected annual income.";
        incomePaymentRatio = 0; 
    }
} else {
    // This block should ideally not be hit with 'required=true' inputs, but handles edge cases.
    monthlyPayment = 0;
    totalInterestPaid = 0;
    totalPaid = 0;
    incomePaymentRatio = 0;
    affordabilityMessage = "Please enter valid loan details to calculate.";
}

return {
    monthlyPayment,
    totalInterestPaid,
    totalPaid,
    incomePaymentRatio,
    affordabilityMessage
};
