formula:
const current_savings = inputs.current_savings;
const annual_contribution = inputs.annual_contribution;
const years_until_college = inputs.years_until_college;
const current_annual_college_cost = inputs.current_annual_college_cost;
const college_inflation_rate = inputs.college_inflation_rate / 100; // Convert to decimal
const investment_return_rate = inputs.investment_return_rate / 100; // Convert to decimal
const years_in_college = inputs.years_in_college;

// 1. Calculate Future Annual College Cost at start
let future_annual_college_cost_at_start = current_annual_college_cost * Math.pow(1 + college_inflation_rate, years_until_college);

// 2. Calculate Total Future College Cost over all years of college
let total_future_college_cost = 0;
for (let i = 0; i < years_in_college; i++) {
    total_future_college_cost += future_annual_college_cost_at_start * Math.pow(1 + college_inflation_rate, i);
}

// 3. Calculate Projected Savings at College Start
let projected_savings_at_college_start = 0;
// Growth of current savings
projected_savings_at_college_start += current_savings * Math.pow(1 + investment_return_rate, years_until_college);

// Growth of annual contributions (Future Value of Ordinary Annuity)
if (investment_return_rate === 0) {
    projected_savings_at_college_start += annual_contribution * years_until_college;
} else {
    projected_savings_at_college_start += annual_contribution * ((Math.pow(1 + investment_return_rate, years_until_college) - 1) / investment_return_rate);
}

// 4. Funding Gap/Surplus
const funding_gap_surplus = projected_savings_at_college_start - total_future_college_cost;

// 5. Monthly Contribution Needed (if there's a gap)
let monthly_contribution_needed = 0;
if (funding_gap_surplus < 0) {
    const funding_gap = -funding_gap_surplus;
    if (years_until_college <= 0) {
        // If college starts now, the entire gap is immediate, so technically no *future* monthly contribution covers it.
        // Or, if desired, could be gap / (years_in_college * 12) to cover *during* college.
        // For this calculator, we'll assume monthly contributions are *before* college start.
        monthly_contribution_needed = funding_gap / (years_in_college * 12);
    } else if (investment_return_rate === 0) {
        monthly_contribution_needed = funding_gap / years_until_college / 12;
    } else {
        // Calculate additional annual contribution needed to cover the gap
        const additional_annual_contribution_needed = funding_gap * investment_return_rate / (Math.pow(1 + investment_return_rate, years_until_college) - 1);
        monthly_contribution_needed = additional_annual_contribution_needed / 12;
    }
} else {
    monthly_contribution_needed = 0; // No additional contribution needed
}

return {
    projected_future_college_cost: Math.max(0, total_future_college_cost),
    projected_savings_at_college_start: Math.max(0, projected_savings_at_college_start),
    funding_gap_surplus: funding_gap_surplus,
    monthly_contribution_needed: Math.max(0, monthly_contribution_needed)
};
