function calculate({
  initialSavings,
  monthlyContribution,
  years,
  annualReturn,
  tuitionInflation,
  currentAnnualCollegeCost,
  numberOfCollegeYears
}) {
  const r_annual = annualReturn / 100;
  const t_annual = tuitionInflation / 100;

  // Calculate projected savings
  let projectedSavings = 0;

  // Future Value of Initial Savings (compounded annually)
  projectedSavings += initialSavings * Math.pow((1 + r_annual), years);

  // Future Value of Monthly Contributions (annuity, compounded monthly)
  // Convert annual return to monthly return for contributions
  const r_monthly = (1 + r_annual) > 0 ? (Math.pow((1 + r_annual), (1/12)) - 1) : 0;
  const numMonths = years * 12;

  if (r_monthly === 0) {
    projectedSavings += monthlyContribution * numMonths;
  } else {
    projectedSavings += monthlyContribution * ((Math.pow((1 + r_monthly), numMonths) - 1) / r_monthly);
  }

  // Calculate total projected college cost
  let totalProjectedCollegeCost = 0;
  const costInflationFactor = (1 + t_annual);

  if (t_annual === 0) {
    // If no tuition inflation, cost is currentAnnualCollegeCost for 'years' from now,
    // multiplied by number of college years
    const costAtCollegeStart = currentAnnualCollegeCost * Math.pow(1 + t_annual, years); // still apply years factor for initial cost
    totalProjectedCollegeCost = costAtCollegeStart * numberOfCollegeYears;
  } else {
    // Cost of college in the first year it's needed (i.e., 'years' from now)
    const firstYearCostWhenNeeded = currentAnnualCollegeCost * Math.pow(costInflationFactor, years);

    // Sum of costs for numberOfCollegeYears, inflating each year during college
    // This is a geometric series sum: A * (X^N - 1) / (X - 1)
    // where A = firstYearCostWhenNeeded, X = costInflationFactor, N = numberOfCollegeYears
    totalProjectedCollegeCost = firstYearCostWhenNeeded * (Math.pow(costInflationFactor, numberOfCollegeYears) - 1) / (costInflationFactor - 1);
  }

  const fundingGap = projectedSavings - totalProjectedCollegeCost;

  return {
    projectedSavings: Math.max(0, projectedSavings),
    totalProjectedCollegeCost: Math.max(0, totalProjectedCollegeCost),
    fundingGap: fundingGap
  };
}
