const currentAge = parseFloat(inputs.currentAge) || 0;
const collegeAge = parseFloat(inputs.collegeAge) || 18;
const currentSavings = parseFloat(inputs.currentSavings) || 0;
const monthly = parseFloat(inputs.monthlyContribution) || 0;
const currentCost = parseFloat(inputs.currentAnnualCost) || 0;
const r = (parseFloat(inputs.annualReturn) || 0) / 100;
const inf = (parseFloat(inputs.tuitionInflation) || 0) / 100;

const yearsUntilCollege = Math.max(0, collegeAge - currentAge);
const months = yearsUntilCollege * 12;

// Future Value of Savings
// FV = PV(1+r)^n + PMT[((1+r/12)^(12n) - 1)/(r/12)]
const fvInitial = currentSavings * Math.pow(1 + r, yearsUntilCollege);
let fvContrib = 0;
if (r === 0) {
    fvContrib = monthly * months;
} else {
    fvContrib = monthly * ((Math.pow(1 + r/12, months) - 1) / (r/12));
}
const totalSavings = fvInitial + fvContrib;

// Future Cost of College (4 Years)
// First year cost = Current * (1+inf)^years
const year1 = currentCost * Math.pow(1 + inf, yearsUntilCollege);
const year2 = year1 * (1 + inf);
const year3 = year2 * (1 + inf);
const year4 = year3 * (1 + inf);
const totalCost = year1 + year2 + year3 + year4;

return {
    totalProjectedCost: totalCost,
    projectedSavings: totalSavings,
    fundingGap: totalCost - totalSavings
};
