const grossPotentialRent = inputs.grossPotentialRent;
const vacancyRate = inputs.vacancyRate / 100;
const otherIncome = inputs.otherIncome;
const totalOperatingExpenses = inputs.totalOperatingExpenses;
const purchasePrice = inputs.purchasePrice;

if (isNaN(grossPotentialRent) || isNaN(vacancyRate) || isNaN(otherIncome) || isNaN(totalOperatingExpenses) || isNaN(purchasePrice) ||
    grossPotentialRent < 0 || vacancyRate < 0 || otherIncome < 0 || totalOperatingExpenses < 0 || purchasePrice <= 0) {
    return {
        noi: NaN,
        capRate: NaN
    };
}

const effectiveGrossIncome = grossPotentialRent * (1 - vacancyRate) + otherIncome;
const noi = effectiveGrossIncome - totalOperatingExpenses;

let capRate = NaN;
if (purchasePrice > 0) {
    capRate = (noi / purchasePrice) * 100;
}

return {
    noi: noi,
    capRate: capRate
};
