const projectLifetime = 25; // years
const solarOutputPerKwYear = 1400; // Average kWh/kW/year in many regions. Can vary.
const batteryValueFactor = 0.07; // 7% additional value from battery storage (peak shaving, enhanced self-consumption, resilience). This is an approximation.

// --- Input Validations and Initial Calculations ---
// Ensure percentage inputs are treated as decimals for calculation
const federalITCRateDecimal = inputs.federalITCRate / 100;
const annualOandMRateDecimal = inputs.annualOandMRate / 100;
const energyInflationRateDecimal = inputs.energyInflationRate / 100;
const solarDegradationRateDecimal = inputs.solarDegradationRate / 100;
const discountRateDecimal = inputs.discountRate / 100;

// Initial Investment after ITC
let initialInvestment = inputs.systemCost * (1 - federalITCRateDecimal);

// Ensure investment is not negative
if (initialInvestment < 0) {
    initialInvestment = 0; // Cap at zero if ITC somehow exceeds cost, though unlikely for real systems.
}

// Variables for annual iteration
let annualSolarProduction = inputs.systemSizeSolar * solarOutputPerKwYear; // kWh in Year 1
let currentElectricityPrice = inputs.averageElectricityPrice;
let annualOMCost = inputs.systemCost * annualOandMRateDecimal;

let cumulativeCashFlow = -initialInvestment;
let totalSavings = 0;
let paybackPeriod = -1;
const annualCashFlows = []; // To store cash flows for NPV calculation

// --- Simulate Cash Flows Over Project Lifetime ---
for (let year = 1; year <= projectLifetime; year++) {
    // Energy Savings for the current year
    const energySavedThisYear = Math.min(annualSolarProduction, inputs.annualElectricityConsumption);
    let annualEnergySavings = energySavedThisYear * currentElectricityPrice;

    // Apply battery value factor (e.g., due to peak shaving, increased self-consumption, demand charge reduction)
    annualEnergySavings *= (1 + batteryValueFactor);

    // Net cash flow for the current year (Savings - O&M)
    const netCashFlowThisYear = annualEnergySavings - annualOMCost;
    annualCashFlows.push(netCashFlowThisYear); // Store for NPV

    // Update cumulative cash flow and total savings
    cumulativeCashFlow += netCashFlowThisYear;
    totalSavings += netCashFlowThisYear;

    // Determine payback period
    if (paybackPeriod === -1 && cumulativeCashFlow >= 0) {
        paybackPeriod = year;
    }

    // --- Apply annual adjustments for next year's calculation ---
    currentElectricityPrice *= (1 + energyInflationRateDecimal); // Electricity price inflates
    annualOMCost *= (1 + energyInflationRateDecimal); // Assume O&M also inflates
    annualSolarProduction *= (1 - solarDegradationRateDecimal); // Solar production degrades
}

// --- Calculate Net Present Value (NPV) ---
let npv = -initialInvestment;
for (let i = 0; i < annualCashFlows.length; i++) {
    npv += annualCashFlows[i] / Math.pow(1 + discountRateDecimal, i + 1);
}

// --- Calculate Annual Savings for Year 1 (for output) ---
const annualSolarProductionYear1 = inputs.systemSizeSolar * solarOutputPerKwYear;
const energySavedYear1 = Math.min(annualSolarProductionYear1, inputs.annualElectricityConsumption);
let annualEnergySavingsYear1 = energySavedYear1 * inputs.averageElectricityPrice;
annualEnergySavingsYear1 *= (1 + batteryValueFactor);
const annualSavingsYear1 = annualEnergySavingsYear1 - (inputs.systemCost * annualOandMRateDecimal);

// --- Return Results ---
return {
    paybackPeriod: paybackPeriod === -1 ? "Over " + projectLifetime + " years" : paybackPeriod.toFixed(1),
    totalSavings25Years: Math.max(0, totalSavings),
    netPresentValue25Years: npv,
    annualSavingsYear1: annualSavingsYear1
};
