function calculate({ totalStateDeploymentCost, stateTotalPopulation, communityPopulation, stateTotalPropertyValue, communityTotalPropertyValue, stateTotalTaxRevenue, communityTotalTaxRevenue, populationWeight, propertyValueWeight, taxRevenueWeight }) {

    const inputs = {
        totalStateDeploymentCost: Number(totalStateDeploymentCost),
        stateTotalPopulation: Number(stateTotalPopulation),
        communityPopulation: Number(communityPopulation),
        stateTotalPropertyValue: Number(stateTotalPropertyValue),
        communityTotalPropertyValue: Number(communityTotalPropertyValue),
        stateTotalTaxRevenue: Number(stateTotalTaxRevenue),
        communityTotalTaxRevenue: Number(communityTotalTaxRevenue),
        populationWeight: Number(populationWeight || 0),
        propertyValueWeight: Number(propertyValueWeight || 0),
        taxRevenueWeight: Number(taxRevenueWeight || 0)
    };

    // Input validation and edge case handling
    for (const key in inputs) {
        if (isNaN(inputs[key]) || inputs[key] < 0) {
            return { error: `Invalid input for ${key}. Please provide a non-negative number.` };
        }
    }
    if (inputs.totalStateDeploymentCost === 0) {
        return { estimatedCommunityCost: 0, overallProportionalityFactor: 0, populationProportion: 0, propertyValueProportion: 0, taxRevenueProportion: 0 };
    }

    // Calculate individual proportionality factors
    const populationProportion = (inputs.stateTotalPopulation > 0) ? (inputs.communityPopulation / inputs.stateTotalPopulation) : 0;
    const propertyValueProportion = (inputs.stateTotalPropertyValue > 0) ? (inputs.communityTotalPropertyValue / inputs.stateTotalPropertyValue) : 0;
    const taxRevenueProportion = (inputs.stateTotalTaxRevenue > 0) ? (inputs.communityTotalTaxRevenue / inputs.stateTotalTaxRevenue) : 0;

    // Normalize weights
    let finalPopWeight = inputs.populationWeight;
    let finalPropWeight = inputs.propertyValueWeight;
    let finalTaxWeight = inputs.taxRevenueWeight;

    const totalWeightSum = finalPopWeight + finalPropWeight + finalTaxWeight;

    if (totalWeightSum === 0) {
        // Default to equal weighting if no weights are provided or sum to zero
        finalPopWeight = 1; 
        finalPropWeight = 1; 
        finalTaxWeight = 1; 
        const defaultTotalWeightSum = 3; // sum of default equal weights
        finalPopWeight /= defaultTotalWeightSum;
        finalPropWeight /= defaultTotalWeightSum;
        finalTaxWeight /= defaultTotalWeightSum;
    } else {
        finalPopWeight /= totalWeightSum;
        finalPropWeight /= totalWeightSum;
        finalTaxWeight /= totalWeightSum;
    }

    // Calculate the overall weighted proportionality factor
    const overallProportionalityFactor = 
        (populationProportion * finalPopWeight) + 
        (propertyValueProportion * finalPropWeight) + 
        (taxRevenueProportion * finalTaxWeight);

    // Calculate the estimated community cost
    const estimatedCommunityCost = inputs.totalStateDeploymentCost * overallProportionalityFactor;

    return {
        estimatedCommunityCost: estimatedCommunityCost,
        overallProportionalityFactor: overallProportionalityFactor,
        populationProportion: populationProportion,
        propertyValueProportion: propertyValueProportion,
        taxRevenueProportion: taxRevenueProportion
    };
}
