function calculatePreparedness(inputs) {
  const { shelter_capacity_percent, comm_channels_available, drill_participation_rate, volunteer_network_density, infra_resilience_score, medical_resource_index, hazard_plan_coverage } = inputs;

  // Input validation and clamping to ensure scores are within expected ranges
  const shelterCap = Math.max(0, Math.min(100, shelter_capacity_percent));
  const commChannels = Math.max(0, Math.min(5, comm_channels_available));
  const drillRate = Math.max(0, Math.min(100, drill_participation_rate));
  const volunteerDensity = Math.max(0, volunteer_network_density);
  const infraScore = Math.max(1, Math.min(5, infra_resilience_score));
  const medicalIndex = Math.max(1, Math.min(5, medical_resource_index));
  const hazardCoverage = Math.max(0, Math.min(100, hazard_plan_coverage));

  // Step 1: Normalize inputs to a common scoring scale (0-5 points for each component)
  // Max score for each component is 5 points.

  // Shelter Capacity: 100% capacity -> 5 points
  const scoreShelter = (shelterCap / 100) * 5;

  // Communication Channels: 5 channels -> 5 points
  const scoreComm = (commChannels / 5) * 5; 

  // Drill Participation: 100% participation -> 5 points
  const scoreDrill = (drillRate / 100) * 5;

  // Volunteer Density: Assume 10 volunteers/1000 population is optimal for 5 points
  const scoreVolunteer = Math.min(5, (volunteerDensity / 10) * 5);

  // Infrastructure Resilience: Direct mapping from 1-5 score
  const scoreInfra = infraScore;

  // Medical Resources: Direct mapping from 1-5 index
  const scoreMedical = medicalIndex;

  // Hazard Plan Coverage: 100% coverage -> 5 points
  const scoreHazard = (hazardCoverage / 100) * 5;

  // Step 2: Apply category weights to calculated scores
  // Weights reflect the relative importance of each factor. Total Max Weighted Score = 42.5
  const weightedShelter = scoreShelter * 1.0; // Moderate importance
  const weightedComm = scoreComm * 1.5;     // High importance, especially for health
  const weightedDrill = scoreDrill * 1.0;   // Moderate importance
  const weightedVolunteer = scoreVolunteer * 1.0; // Moderate importance
  const weightedInfra = scoreInfra * 1.5;   // High importance for recovery
  const weightedMedical = scoreMedical * 1.5; // Critical for health outcomes
  const weightedHazard = scoreHazard * 1.0; // Moderate importance

  // Step 3: Sum the weighted scores to get a raw preparedness total
  const rawTotalScore = weightedShelter + weightedComm + weightedDrill + weightedVolunteer + weightedInfra + weightedMedical + weightedHazard;

  // Step 4: Normalize the raw total score to a 0-100 scale
  const maxPossibleRawScore = (5 * 1.0) + (5 * 1.5) + (5 * 1.0) + (5 * 1.0) + (5 * 1.5) + (5 * 1.5) + (5 * 1.0); // 5 + 7.5 + 5 + 5 + 7.5 + 7.5 + 5 = 42.5
  let preparedness_score = (rawTotalScore / maxPossibleRawScore) * 100;

  // Cap score at 100 and ensure non-negative
  preparedness_score = Math.max(0, Math.min(100, preparedness_score));

  // Step 5: Determine the qualitative preparedness level based on the final score
  let preparedness_level;
  if (preparedness_score < 30) {
    preparedness_level = "Critical Need for Improvement";
  } else if (preparedness_score < 50) {
    preparedness_level = "Developing Preparedness";
  } else if (preparedness_score < 70) {
    preparedness_level = "Adequate Preparedness";
  } else if (preparedness_score < 85) {
    preparedness_level = "Strong Preparedness";
  } else {
    preparedness_level = "Exemplary Preparedness";
  }

  return {
    preparedness_score: parseFloat(preparedness_score.toFixed(1)),
    preparedness_level: preparedness_level
  };
}
