function calculate({ community_vaccination_rate, population_density_sq_km, r0_value, average_contact_rate_daily, incubation_period_days, duration_of_infectiousness_days, healthcare_capacity_index, vulnerable_population_percentage, international_travel_exposure }) {
    // Input validation and normalization
    community_vaccination_rate = Math.max(0, Math.min(100, community_vaccination_rate));
    population_density_sq_km = Math.max(10, population_density_sq_km); // Min density for a 'community'
    r0_value = Math.max(0.5, Math.min(20, r0_value)); // Reasonable R0 range
    average_contact_rate_daily = Math.max(1, Math.min(30, average_contact_rate_daily));
    incubation_period_days = Math.max(0.5, Math.min(60, incubation_period_days));
    duration_of_infectiousness_days = Math.max(0.5, Math.min(30, duration_of_infectiousness_days));
    healthcare_capacity_index = Math.max(0, Math.min(10, healthcare_capacity_index));
    vulnerable_population_percentage = Math.max(0, Math.min(100, vulnerable_population_percentage));
    international_travel_exposure = Math.max(0, Math.min(10, international_travel_exposure));

    // --- Step 1: Calculate Outbreak Probability --- (Scale: 0-100%)
    // Factors influencing probability:
    // 1. Susceptibility: Lower vaccination rate -> higher probability
    let susceptibilityFactor = (100 - community_vaccination_rate) / 100;

    // 2. Contagion Potential (R0): Higher R0 -> higher probability
    // Normalized R0 around 3.5 (e.g., flu R0 is 1.5-2, measles is 12-18). A factor of 1 at R0=3.5.
    let r0ProbFactor = r0_value / 3.5; 

    // 3. Population Density: Higher density -> higher probability
    // Using log10 to account for diminishing returns of extreme density, normalized around 500 people/km².
    let densityProbFactor = Math.log10(population_density_sq_km + 1) / Math.log10(500 + 1); 

    // 4. Contact Rate: Higher contact rate -> higher probability
    // Normalized contact rate around 10 contacts/day.
    let contactProbFactor = average_contact_rate_daily / 10; 

    // 5. External Introduction Risk: Higher travel exposure -> higher probability
    // Normalized travel exposure around 5 (mid-range).
    let travelProbFactor = international_travel_exposure / 5; 

    let rawOutbreakProbability = susceptibilityFactor * r0ProbFactor * densityProbFactor * contactProbFactor * travelProbFactor * 100;

    // Apply caps and floors for probability
    let outbreak_probability = Math.max(0, Math.min(100, rawOutbreakProbability));

    // Adjust probability based on extreme values for better realism
    if (community_vaccination_rate >= 95 && r0_value <= 1.5) {
        outbreak_probability *= 0.15; // Significantly reduced probability for high vaccination and low R0
    } else if (community_vaccination_rate <= 50 && r0_value >= 5) {
        outbreak_probability = Math.min(99.9, outbreak_probability * 1.5); // Increased probability for low vaccination and high R0
    }
    outbreak_probability = Math.max(0.1, Math.min(100, outbreak_probability)); // Ensure it's never exactly zero, and max 100


    // --- Step 2: Calculate Potential Impact Score --- (Scale: 0-10)
    // Factors influencing impact:
    // 1. Disease Severity Potential: R0, duration of infectiousness, incubation period
    //    Higher R0 and duration, lower incubation period suggest faster spread and more cases, increasing severity.
    let severityImpactFactor = (r0_value * duration_of_infectiousness_days) / (incubation_period_days || 1); // Avoid division by zero

    // 2. Vulnerable Population: Higher percentage -> higher impact
    // Normalized around 30% vulnerability for a factor of 1.
    let vulnerableImpactFactor = vulnerable_population_percentage / 30; 

    // 3. Healthcare Capacity: Higher capacity -> lower impact
    // Inverted scale: 0 healthcare capacity -> factor 1, 10 -> factor 0.1 (never zero impact).
    let healthcareImpactFactor = (10 - healthcare_capacity_index) / 10; 
    healthcareImpactFactor = Math.max(0.1, healthcareImpactFactor); // Ensure there's always some baseline impact potential

    // Combine factors for raw impact score
    // Normalize to a 0-10 scale. Divisor '2' is a scaling constant.
    let rawPotentialImpact = (severityImpactFactor / 2) * vulnerableImpactFactor * healthcareImpactFactor * 3.5; 

    // Apply caps and floors for impact score
    let potential_impact_score = Math.max(0, Math.min(10, rawPotentialImpact));

    // Edge cases for impact:
    if (healthcare_capacity_index >= 9 && vulnerable_population_percentage <= 10) {
        potential_impact_score *= 0.4; // Significantly reduced impact for high capacity, low vulnerability
    } else if (healthcare_capacity_index <= 2 && vulnerable_population_percentage >= 70) {
        potential_impact_score = Math.min(9.8, potential_impact_score * 1.6); // Increased impact for low capacity, high vulnerability
    }
    potential_impact_score = Math.max(0.2, Math.min(10, potential_impact_score)); // Ensure never exactly zero and max 10

    // --- Step 3: Estimate Peak Cases --- (Number of cases)
    // This is a simplified estimation, not a full epidemiological model. It scales with probability, 
    // a proxy for community population, remaining susceptible population, and disease transmissibility.

    // A rough scaling factor for a 'local' community affected based on density. Max density 5000 -> 5000*5 = 25000 population proxy.
    let communitySizeProxy = population_density_sq_km * 5; 

    // Fraction of population susceptible to infection
    let susceptiblePopulationFraction = (100 - community_vaccination_rate) / 100;

    // Base estimation: Probability * Proxy Population * Susceptible Fraction * Transmissibility (R0/2 for scaling)
    let estimated_peak_cases_raw = (outbreak_probability / 100) * communitySizeProxy * susceptiblePopulationFraction * (r0_value / 2.5); 

    // Further adjust by healthcare capacity (lower capacity -> more 'unmanaged' or severe cases) 
    // and vulnerable population (higher vulnerability -> more severe cases needing detection/treatment).
    estimated_peak_cases_raw = estimated_peak_cases_raw * (1 + vulnerable_population_percentage / 150) * (1 + (10 - healthcare_capacity_index) / 15);

    // Apply caps and floors for peak cases
    // Maximum possible cases for a given density (e.g., 20% of density*100 for a small urban area).
    let max_possible_cases_for_density = population_density_sq_km * 20;
    let estimated_peak_cases = Math.round(Math.max(0, Math.min(max_possible_cases_for_density, estimated_peak_cases_raw)));

    // If probability is extremely low, peak cases should be 0 or very small
    if (outbreak_probability < 1) {
        estimated_peak_cases = Math.round(estimated_peak_cases * (outbreak_probability / 5)); // Scale down significantly
        if (estimated_peak_cases < 10 && outbreak_probability < 0.5) estimated_peak_cases = 0;
    } else if (estimated_peak_cases < 10 && outbreak_probability > 5) { // Ensure small number for moderate probability
         estimated_peak_cases = 10; 
    }

    return {
        outbreak_probability: outbreak_probability,
        potential_impact_score: potential_impact_score,
        estimated_peak_cases: estimated_peak_cases
    };
}
