formula:
let currentBalance = inputs.initial_program_balance;
let annualCoreFundingAdjusted = inputs.annual_core_funding;
let yearsToDepletion = null;

for (let year = 1; year <= inputs.projection_years; year++) {
    const totalAnnualIncomeForYear = annualCoreFundingAdjusted + inputs.annual_fundraising_goal;
    const netAnnualChange = totalAnnualIncomeForYear - inputs.program_expenditure_rate;

    currentBalance += netAnnualChange;

    if (currentBalance <= 0 && yearsToDepletion === null) {
        yearsToDepletion = year;
    }

    annualCoreFundingAdjusted *= (1 + inputs.funding_change_percentage / 100);
    annualCoreFundingAdjusted = Math.max(0, annualCoreFundingAdjusted); // Core funding cannot go negative
}

let sustainabilityStatus;
if (currentBalance > 0) {
    sustainabilityStatus = "Sustainable";
    yearsToDepletion = null; // Ensure null if sustainable
} else {
    sustainabilityStatus = "Unsustainable";
    if (yearsToDepletion === null) {
        yearsToDepletion = inputs.projection_years; // If it only went negative on the last year
    }
}

return {
    final_projected_balance: currentBalance,
    sustainability_status: sustainabilityStatus,
    years_to_depletion: yearsToDepletion
};
