function calculateCapacity(inputs) {
    const {
        totalShelterAreaSqFt,
        minSqFtPerPerson,
        availableStaff,
        staffResidentRatio,
        availableCots,
        cotUtilizationRate,
        totalFoodRationPersonDays,
        medicalKitsAvailable,
        specialNeedsProportion
    } = inputs;

    // --- Sanitize Inputs ---
    const effectiveMinSqFtPerPerson = Math.max(1, minSqFtPerPerson);
    const effectiveCotUtilizationRate = Math.max(0, Math.min(100, cotUtilizationRate)) / 100;
    const effectiveSpecialNeedsProportion = Math.max(0, Math.min(100, specialNeedsProportion)) / 100;
    const effectiveStaffResidentRatio = Math.max(0.001, staffResidentRatio); // Prevent division by zero, min 1:1000 ratio

    // --- Constants ---
    const medicalKitRatio = 50; // 1 medical kit per 50 residents (guideline)
    const minimumRequiredFoodDurationDays = 3; // Standard minimum food duration for initial emergency response
    const specialNeedsSpaceMultiplier = 1.5; // Special needs residents require 1.5x normal space

    // --- Calculations for individual capacity limits ---

    // 1. Capacity by Space
    // Adjusted space per person considering special needs:
    // Total Area = N * [ (1-SN%) * minSqFtPerPerson + SN% * minSqFtPerPerson * specialNeedsSpaceMultiplier ]
    // N = Total Area / [ minSqFtPerPerson * (1 - SN% + SN% * specialNeedsSpaceMultiplier) ]
    const adjustedSqFtPerPerson = effectiveMinSqFtPerPerson * (1 - effectiveSpecialNeedsProportion + effectiveSpecialNeedsProportion * specialNeedsSpaceMultiplier);
    const maxCapacityBySpace = totalShelterAreaSqFt > 0 && adjustedSqFtPerPerson > 0 ? Math.floor(totalShelterAreaSqFt / adjustedSqFtPerPerson) : 0;

    // 2. Capacity by Staff
    // Capacity = Available Staff / Staff-to-Resident Ratio
    const maxCapacityByStaff = availableStaff > 0 && effectiveStaffResidentRatio > 0 ? Math.floor(availableStaff / effectiveStaffResidentRatio) : 0;

    // 3. Capacity by Cots
    const deployableCots = Math.floor(availableCots * effectiveCotUtilizationRate);
    const maxCapacityByCots = deployableCots; // Assuming one cot per person

    // 4. Capacity by Food Rations
    // Capacity = Total Person-Days of Food / Minimum Required Food Duration (e.g., 3 days)
    const maxCapacityByFoodDuration = totalFoodRationPersonDays > 0 && minimumRequiredFoodDurationDays > 0 ? Math.floor(totalFoodRationPersonDays / minimumRequiredFoodDurationDays) : 0;

    // 5. Capacity by Medical Kits
    // Capacity = Available Medical Kits * Medical Kit Ratio (e.g., 50 residents per kit)
    const maxCapacityByMedicalKits = medicalKitsAvailable > 0 && medicalKitRatio > 0 ? Math.floor(medicalKitsAvailable * medicalKitRatio) : 0;

    // --- Determine Overall Capacity ---
    const capacities = [
        maxCapacityBySpace,
        maxCapacityByStaff,
        maxCapacityByCots,
        maxCapacityByFoodDuration,
        maxCapacityByMedicalKits
    ].filter(c => c > 0); // Filter out zero capacities to avoid min of zero if some resources are truly zero

    const overallCalculatedCapacity = capacities.length > 0 ? Math.min(...capacities) : 0;

    // --- Calculate Deficits (if overall capacity is limited by a specific factor) ---
    let staffDeficit = 0;
    let cotDeficit = 0;
    let spaceDeficitSqFt = 0;
    let medicalKitDeficit = 0;

    if (overallCalculatedCapacity > 0) {
        // Staff deficit
        const requiredStaff = Math.ceil(overallCalculatedCapacity * effectiveStaffResidentRatio);
        if (availableStaff < requiredStaff) {
            staffDeficit = requiredStaff - availableStaff;
        }

        // Cot deficit
        const requiredDeployableCots = overallCalculatedCapacity;
        if (deployableCots < requiredDeployableCots) {
            // Need to calculate how many *additional* physical cots, considering utilization rate
            const neededCotsRaw = requiredDeployableCots / effectiveCotUtilizationRate;
            cotDeficit = Math.ceil(neededCotsRaw) - availableCots;
            if (cotDeficit < 0) cotDeficit = 0; // Should not be negative if already exceeding needs
        }

        // Space deficit
        const requiredSpace = Math.ceil(overallCalculatedCapacity * adjustedSqFtPerPerson);
        if (totalShelterAreaSqFt < requiredSpace) {
            spaceDeficitSqFt = requiredSpace - totalShelterAreaSqFt;
        }

        // Medical kit deficit
        const requiredMedicalKits = Math.ceil(overallCalculatedCapacity / medicalKitRatio);
        if (medicalKitsAvailable < requiredMedicalKits) {
            medicalKitDeficit = requiredMedicalKits - medicalKitsAvailable;
        }
    }

    // --- Calculate Food Duration for Overall Capacity ---
    let foodDurationForOverallCapacity = 0;
    if (overallCalculatedCapacity > 0 && totalFoodRationPersonDays > 0) {
        foodDurationForOverallCapacity = totalFoodRationPersonDays / overallCalculatedCapacity;
    }

    return {
        maxCapacityBySpace: Math.max(0, maxCapacityBySpace),
        maxCapacityByStaff: Math.max(0, maxCapacityByStaff),
        maxCapacityByCots: Math.max(0, maxCapacityByCots),
        maxCapacityByFoodDuration: Math.max(0, maxCapacityByFoodDuration),
        maxCapacityByMedicalKits: Math.max(0, maxCapacityByMedicalKits),
        overallCalculatedCapacity: Math.max(0, overallCalculatedCapacity),
        staffDeficit: Math.max(0, staffDeficit),
        cotDeficit: Math.max(0, cotDeficit),
        spaceDeficitSqFt: Math.max(0, spaceDeficitSqFt),
        medicalKitDeficit: Math.max(0, medicalKitDeficit),
        foodDurationForOverallCapacity: Math.max(0, foodDurationForOverallCapacity)
    };
}
