function calculateSafetyRisk(inputs) {
    const { estimatedAttendees, eventCategory, venueType, securityPersonnelCount, policeCoordinationLevel, medicalAidStations, alcoholService, potentialForConflict, localIncidentHistoryScore, emergencyExitsAdequacy } = inputs;

    let baseRisk = 0;

    // 1. Base Risk based on Event Category (Higher values mean higher inherent risk)
    switch (eventCategory) {
        case 'protest_rally': baseRisk += 40; break;
        case 'music_concert': baseRisk += 35; break;
        case 'cultural_festival': baseRisk += 25; break;
        case 'sporting_event': baseRisk += 30; break;
        case 'community_fair': baseRisk += 20; break;
        case 'corporate_event': baseRisk += 10; break;
        case 'parade': baseRisk += 30; break;
    }

    // 2. Adjust for Venue Type
    switch (venueType) {
        case 'open_public_space': baseRisk += 15; break;
        case 'enclosed_arena_stadium': baseRisk += 10; break;
        case 'street_parade_route': baseRisk += 15; break;
        case 'park_green_space': baseRisk += 5; break;
        case 'private_venue_controlled': baseRisk -= 5; break; // Lower risk for controlled environments
    }

    // 3. Adjust for Estimated Attendees (Exponential growth in risk)
    let crowdRisk = 0;
    if (estimatedAttendees < 100) crowdRisk = 0;
    else if (estimatedAttendees < 1000) crowdRisk = 5;
    else if (estimatedAttendees < 5000) crowdRisk = 10;
    else if (estimatedAttendees < 15000) crowdRisk = 20;
    else if (estimatedAttendees < 50000) crowdRisk = 35;
    else crowdRisk = 50; // Very large crowds
    baseRisk += crowdRisk;

    // 4. Security Personnel Ratio (Mitigation factor)
    // A lower ratio (more attendees per security) means higher risk
    const attendeesPerSecurity = securityPersonnelCount > 0 ? estimatedAttendees / securityPersonnelCount : estimatedAttendees;
    let securityRisk = 0;
    if (attendeesPerSecurity > 250) securityRisk = 25;
    else if (attendeesPerSecurity > 100) securityRisk = 15;
    else if (attendeesPerSecurity > 50) securityRisk = 5;
    else if (attendeesPerSecurity > 25) securityRisk = 0;
    else securityRisk = -5; // Good security ratio
    baseRisk += securityRisk;

    // 5. Police/Emergency Services Coordination
    switch (policeCoordinationLevel) {
        case 'poor': baseRisk += 20; break;
        case 'fair': baseRisk += 10; break;
        case 'good': baseRisk -= 5; break;
        case 'excellent': baseRisk -= 10; break;
    }

    // 6. Medical Aid Stations (Mitigation factor)
    // Assume a baseline need, fewer stations for large crowds increases risk
    const recommendedMedicalStations = Math.ceil(estimatedAttendees / 5000) + 1; // 1 per 5000 attendees, plus a base of 1
    let medicalRisk = 0;
    if (medicalAidStations < recommendedMedicalStations / 2) medicalRisk = 15;
    else if (medicalAidStations < recommendedMedicalStations) medicalRisk = 5;
    else medicalRisk = -5; // Adequate or more medical stations
    baseRisk += medicalRisk;

    // 7. Alcohol Service
    if (alcoholService === 'yes') {
        baseRisk += 10; // Alcohol increases potential for incidents
    }

    // 8. Potential for External Conflict/Disruption
    switch (potentialForConflict) {
        case 'high': baseRisk += 25; break;
        case 'moderate': baseRisk += 10; break;
        case 'low': baseRisk += 0; break; // No additional risk
    }

    // 9. Local Incident History Score (Directly proportional to risk)
    baseRisk += (localIncidentHistoryScore - 1) * 2.5; // Scale 1-10 to 0-22.5

    // 10. Adequacy of Emergency Exits & Evacuation Routes
    switch (emergencyExitsAdequacy) {
        case 'poor': baseRisk += 20; break;
        case 'fair': baseRisk += 10; break;
        case 'good': baseRisk -= 5; break;
        case 'excellent': baseRisk -= 10; break;
    }

    // Cap and Floor the raw risk score
    let finalRiskScore = Math.max(0, Math.min(100, baseRisk));

    let riskCategory = '';
    if (finalRiskScore >= 75) {
        riskCategory = 'Critical Risk';
    } else if (finalRiskScore >= 50) {
        riskCategory = 'High Risk';
    } else if (finalRiskScore >= 25) {
        riskCategory = 'Moderate Risk';
    } else {
        riskCategory = 'Low Risk';
    }

    return {
        safetyRiskScore: finalRiskScore,
        riskCategory: riskCategory
    };
}
