const participants = inputs.eventParticipants;
const durationHours = inputs.eventDurationHours;
const safetyShift = inputs.perceivedSafetyShift; // -5 to 5
const mediaPolarization = inputs.mediaNarrativePolarization; // 0 to 10
const trustShift = inputs.trustInInstitutionsShift; // -5 to 5
const economicDisruption = inputs.economicDisruptionScore; // 0 to 10
const dialogueScore = inputs.constructiveDialogueScore; // 0 to 10
const inclusivityScore = inputs.perceivedInclusivityScore; // 0 to 10

// --- Step 1: Normalize and weigh inputs ---

// Scale factor for event magnitude
// Base multiplier: 1 unit for 1000 participants over 12 hours. Max 5.
let scaleFactor = Math.min(5, (participants / 1000) * (durationHours / 12) * 0.75);
if (scaleFactor < 0.1) scaleFactor = 0.1; // Minimum impact even for small events

// --- Impact Component Calculations (normalized to a -10 to 10 base before scaling) ---

// 1. Safety & Well-being Impact (Primary driver of immediate community sentiment)
// safetyShift is -5 to 5. Normalize to -10 to 10 for consistency.
let safetyImpact = safetyShift * 2; // -10 to 10

// economicDisruption is 0 to 10. Invert and normalize to -10 to 0.
let economicImpact = (economicDisruption / 10) * -10; // 0 to -10

let wellbeingBaseImpact = safetyImpact * 0.6 + economicImpact * 0.4; // Weighted average

// 2. Trust Dynamics Impact (Focuses on institutional and inter-personal trust)
// trustShift is -5 to 5. Normalize to -10 to 10.
let directTrustImpact = trustShift * 2; // -10 to 10

// mediaPolarization is 0 to 10. Invert and normalize to 0 to -10.
let mediaTrustImpact = (mediaPolarization / 10) * -10; // 0 to -10

// dialogueScore is 0 to 10. Normalize to 0 to 10.
let dialogueTrustImpact = (dialogueScore / 10) * 10; // 0 to 10

let trustBaseImpact = directTrustImpact * 0.5 + mediaTrustImpact * 0.3 + dialogueTrustImpact * 0.2; // Weighted average

// 3. Cohesion & Inclusivity Impact (Direct measure of community togetherness)
// inclusivityScore is 0 to 10. Normalize to 0 to 10.
let inclusivityImpact = (inclusivityScore / 10) * 10; // 0 to 10

// Cross-impact from wellbeing and trust
let wellbeingToCohesion = wellbeingBaseImpact * 0.3; // Wellbeing influences cohesion
let trustToCohesion = trustBaseImpact * 0.4; // Trust heavily influences cohesion

// --- Step 2: Combine components for final scores ---

// Calculate Community Well-being Shift Indicator
// This is an intermediate output, reflecting perceived well-being.
let wellbeingShiftIndicator = wellbeingBaseImpact * scaleFactor * 2; // Scale and make more impactful
wellbeingShiftIndicator = Math.round(Math.max(-100, Math.min(100, wellbeingShiftIndicator)));


// Calculate Community Trust Index
// Combines direct trust factors with the effect of dialogue.
let communityTrustIndex = trustBaseImpact * scaleFactor * 2;
communityTrustIndex = Math.round(Math.max(-100, Math.min(100, communityTrustIndex)));

// Calculate Social Cohesion Impact Score
let cohesionBaseImpact = inclusivityImpact * 0.3 + wellbeingToCohesion * 0.35 + trustToCohesion * 0.35; // Weighted
let cohesionImpactScore = cohesionBaseImpact * scaleFactor * 3; // Apply a larger scale factor for overall cohesion

// Cap the final score
cohesionImpactScore = Math.round(Math.max(-100, Math.min(100, cohesionImpactScore)));

// Edge case: Very low participants, very short duration
if (participants < 10 && durationHours < 1) {
    cohesionImpactScore = 0;
    communityTrustIndex = 0;
    wellbeingShiftIndicator = 0;
}

return {
    cohesionImpactScore: cohesionImpactScore,
    communityTrustIndex: communityTrustIndex,
    wellbeingShiftIndicator: wellbeingShiftIndicator
};
