function calculateCHABI(inputs) {
  const {
    closureDurationDays,
    affectedPopulation,
    preExistingVulnerability,
    essentialSupplyDependency,
    healthInfrastructureCapacity,
    medicalPersonnelRetention,
    alternativeRouteViability,
    severityOfHealthCrisis
  } = inputs;

  // Input validation and default to 0 for non-numeric or negative values, though min/max in inputs should prevent this.
  const safeDuration = Math.max(0, parseFloat(closureDurationDays) || 0);
  const safePopulation = Math.max(0, parseFloat(affectedPopulation) || 0);
  const safeVulnerability = Math.max(1, Math.min(10, parseFloat(preExistingVulnerability) || 1));
  const safeSupplyDependency = Math.max(0, Math.min(100, parseFloat(essentialSupplyDependency) || 0));
  const safeInfrastructure = Math.max(0, Math.min(100, parseFloat(healthInfrastructureCapacity) || 0));
  const safePersonnel = Math.max(0, Math.min(100, parseFloat(medicalPersonnelRetention) || 0));
  const safeAltRoutes = Math.max(0, Math.min(5, parseFloat(alternativeRouteViability) || 0));
  const safeCrisisSeverity = Math.max(1, Math.min(10, parseFloat(severityOfHealthCrisis) || 1));

  // Component 1: Duration Impact (Weighted: longer duration -> higher impact)
  // A base of 0.1 points per day. 30 days = 3 points.
  const barrierComponentDuration = safeDuration * 0.1;

  // Component 2: Population Impact (Weighted: larger population -> higher impact)
  // 1 point per 10,000 people. 100,000 people = 10 points.
  const barrierComponentPopulation = safePopulation / 10000;

  // Component 3: Pre-existing Vulnerability Impact (Weighted: higher score -> higher impact)
  // 5 points per 1 point on the 1-10 scale. Max 50 points.
  const barrierComponentVulnerability = safeVulnerability * 5;

  // Component 4: Essential Supply Dependency Impact (Weighted: higher dependency -> higher impact)
  // 0.5 points per 1% dependency. Max 50 points.
  const barrierComponentSupplyDependency = safeSupplyDependency * 0.5;

  // Component 5: Health Infrastructure Capacity Loss Impact (Weighted: lower capacity -> higher impact)
  // 0.4 points per 1% loss of capacity. Max 40 points.
  const barrierComponentInfrastructureLoss = (100 - safeInfrastructure) * 0.4;

  // Component 6: Medical Personnel Availability Loss Impact (Weighted: lower availability -> higher impact)
  // 0.3 points per 1% loss of personnel. Max 30 points.
  const barrierComponentPersonnelLoss = (100 - safePersonnel) * 0.3;

  // Component 7: Alternative Route Viability Impact (Weighted: lower viability -> higher impact)
  // 10 points per 1 point on the 0-5 scale of non-viability (5 - score). Max 50 points.
  const barrierComponentAlternativeRoutes = (5 - safeAltRoutes) * 10;

  // Component 8: Severity of Current Health Crisis Impact (Weighted: higher severity -> higher impact)
  // 8 points per 1 point on the 1-10 scale. Max 80 points.
  const barrierComponentCrisisSeverity = safeCrisisSeverity * 8;

  // Sum all barrier components
  let totalRawBarrier = 
    barrierComponentDuration +
    barrierComponentPopulation +
    barrierComponentVulnerability +
    barrierComponentSupplyDependency +
    barrierComponentInfrastructureLoss +
    barrierComponentPersonnelLoss +
    barrierComponentAlternativeRoutes +
    barrierComponentCrisisSeverity;

  // Cap the CHABI to a maximum of 500 for standardization
  const chabiIndex = Math.min(totalRawBarrier, 500);

  let impactCategory = '';
  if (chabiIndex < 100) {
    impactCategory = 'Low Barrier - Stable Access';
  } else if (chabiIndex <= 250) {
    impactCategory = 'Moderate Barrier - Emerging Concerns';
  } else if (chabiIndex <= 400) {
    impactCategory = 'High Barrier - Significant Disruptions';
  } else {
    impactCategory = 'Critical Barrier - Urgent Intervention Required';
  }

  return {
    chabiIndex: parseFloat(chabiIndex.toFixed(1)),
    impactCategory: impactCategory
  };
}
