formula:
/**
 * Determines optimal distribution of healthcare funding across different community health initiatives.
 * 
 * Inputs:
 * - totalFunding: Total available budget.
 * - populationSize: Total population of the community.
 * - chronicDiseasePrevalence: Percentage of population affected by chronic diseases.
 * - socioeconomicDisadvantageIndex: Index from 0-1, indicating level of disadvantage.
 * - preventativeCareEffectiveness: Cost per unit of impact for preventative care.
 * - specializedServicesEffectiveness: Cost per unit of impact for specialized services.
 * - healthEducationEffectiveness: Cost per unit of impact for health education.
 * - emergencyReservePercent: Percentage of total funding for emergency reserve.
 *
 * Outputs:
 * - allocatedPreventative: Funds for preventative care.
 * - allocatedSpecialized: Funds for specialized services.
 * - allocatedEducation: Funds for health education programs.
 * - emergencyReserve: Funds reserved for emergencies.
 * - unallocatedFunds: Any remaining unallocated balance.
 */

const MIN_ALLOC_PERCENT_PREVENTATIVE = 0.10; // Minimum 10% of adjustable funding
const MIN_ALLOC_PERCENT_SPECIALIZED = 0.15; // Minimum 15% of adjustable funding
const MIN_ALLOC_PERCENT_EDUCATION = 0.05; // Minimum 5% of adjustable funding
const EPSILON = 1e-9; // Small value to prevent division by zero

// Input validation and sanitization
const tf = Math.max(0, inputs.totalFunding || 0);
const ps = Math.max(1, inputs.populationSize || 1);
const cd = Math.min(100, Math.max(0, inputs.chronicDiseasePrevalence || 0));
const sdi = Math.min(1, Math.max(0, inputs.socioeconomicDisadvantageIndex || 0));
const pce = Math.max(EPSILON, inputs.preventativeCareEffectiveness || EPSILON); // Prevent division by zero
const sse = Math.max(EPSILON, inputs.specializedServicesEffectiveness || EPSILON); // Prevent division by zero
const hee = Math.max(EPSILON, inputs.healthEducationEffectiveness || EPSILON); // Prevent division by zero
const erp = Math.min(100, Math.max(0, inputs.emergencyReservePercent || 0));

if (tf === 0) {
  return {
    allocatedPreventative: 0,
    allocatedSpecialized: 0,
    allocatedEducation: 0,
    emergencyReserve: 0,
    unallocatedFunds: 0
  };
}

// 1. Calculate Emergency Reserve
const emergencyReserve = tf * (erp / 100);

// 2. Determine Funds Available for Program Allocation
let adjustableFunding = tf - emergencyReserve;
if (adjustableFunding < 0) {
  adjustableFunding = 0;
}

// 3. Calculate Program Base Scores (Inverse Cost-Effectiveness)
// A lower C/E ratio means higher impact per dollar, thus a higher score.
const scorePreventative = 1 / pce;
const scoreSpecialized = 1 / sse;
const scoreEducation = 1 / hee;

// 4. Calculate Community Health Need Multiplier
// This multiplier reflects the overall severity of health needs in the community.
// Higher chronic disease prevalence and socioeconomic disadvantage lead to a higher need multiplier.
// Weights can be adjusted based on policy priorities.
const needMultiplier = (cd / 100 * 0.6) + (sdi * 0.4); // 60% chronic disease, 40% socioeconomic

// 5. Calculate Weighted Allocation Factors for each program type
// These factors combine cost-effectiveness with the community's overall health need.
const factorPreventative = scorePreventative * (1 + needMultiplier); // Amplified by need
const factorSpecialized = scoreSpecialized * (1 + needMultiplier); // Amplified by need
const factorEducation = scoreEducation * (1 + needMultiplier); // Amplified by need

// 6. Calculate Minimum Floor Allocations for each program type
const minAllocPreventative = adjustableFunding * MIN_ALLOC_PERCENT_PREVENTATIVE;
const minAllocSpecialized = adjustableFunding * MIN_ALLOC_PERCENT_SPECIALIZED;
const minAllocEducation = adjustableFunding * MIN_ALLOC_PERCENT_EDUCATION;

const totalMinimums = minAllocPreventative + minAllocSpecialized + minAllocEducation;

let finalAllocPreventative, finalAllocSpecialized, finalAllocEducation;
let fundsRemainingAfterMinimums;

// Handle case where adjustable funding is less than total minimums required
if (adjustableFunding < totalMinimums) {
    // Proportionally allocate adjustable funding to meet minimums as best as possible
    const ratio = adjustableFunding / totalMinimums;
    finalAllocPreventative = minAllocPreventative * ratio;
    finalAllocSpecialized = minAllocSpecialized * ratio;
    finalAllocEducation = minAllocEducation * ratio;
    fundsRemainingAfterMinimums = 0;
} else {
    // Allocate minimums first
    finalAllocPreventative = minAllocPreventative;
    finalAllocSpecialized = minAllocSpecialized;
    finalAllocEducation = minAllocEducation;
    fundsRemainingAfterMinimums = adjustableFunding - totalMinimums;

    // Distribute remaining funds based on weighted factors
    const totalFactors = factorPreventative + factorSpecialized + factorEducation;
    if (totalFactors > EPSILON) {
        finalAllocPreventative += fundsRemainingAfterMinimums * (factorPreventative / totalFactors);
        finalAllocSpecialized += fundsRemainingAfterMinimums * (factorSpecialized / totalFactors);
        finalAllocEducation += fundsRemainingAfterMinimums * (factorEducation / totalFactors);
    } else {
        // If all factors are zero or negligible, distribute remaining funds equally
        finalAllocPreventative += fundsRemainingAfterMinimums / 3;
        finalAllocSpecialized += fundsRemainingAfterMinimums / 3;
        finalAllocEducation += fundsRemainingAfterMinimums / 3;
    }
}

// Ensure no negative allocations and round to 2 decimal places
finalAllocPreventative = Math.max(0, finalAllocPreventative);
finalAllocSpecialized = Math.max(0, finalAllocSpecialized);
finalAllocEducation = Math.max(0, finalAllocEducation);

// Final check to ensure total allocated funds do not exceed total available funding
let totalAllocatedPrograms = finalAllocPreventative + finalAllocSpecialized + finalAllocEducation;
let currentTotalUsed = totalAllocatedPrograms + emergencyReserve;

// Adjust if floating point errors cause slight overshoot or if minimums logic was problematic
if (currentTotalUsed > tf) {
    const overshoot = currentTotalUsed - tf;
    // Proportionally reduce program allocations to fit budget
    if (totalAllocatedPrograms > EPSILON) {
        const reductionRatio = (totalAllocatedPrograms - overshoot) / totalAllocatedPrograms;
        finalAllocPreventative *= reductionRatio;
        finalAllocSpecialized *= reductionRatio;
        finalAllocEducation *= reductionRatio;
    } else { // If program allocations are already zero, just adjust emergency reserve if needed
        emergencyReserve = Math.max(0, tf - (finalAllocPreventative + finalAllocSpecialized + finalAllocEducation));
    }
}

const finalTotalAllocated = finalAllocPreventative + finalAllocSpecialized + finalAllocEducation + emergencyReserve;
const unallocatedFunds = tf - finalTotalAllocated;

return {
  allocatedPreventative: finalAllocPreventative,
  allocatedSpecialized: finalAllocSpecialized,
  allocatedEducation: finalAllocEducation,
  emergencyReserve: emergencyReserve,
  unallocatedFunds: Math.max(0, unallocatedFunds)
};
