const serviceBudgetRatio = inputs.serviceBudgetRatio; 
const staffVacancyRate = inputs.staffVacancyRate; 
const externalCrisisIndex = inputs.externalCrisisIndex; 
const communityVulnerabilityIndex = inputs.communityVulnerabilityIndex; 
const serviceCriticalityIndex = inputs.serviceCriticalityIndex; 
const recoveryResourceAvailability = inputs.recoveryResourceAvailability; 
const totalCommunityPopulation = inputs.totalCommunityPopulation; 
const existingPreparednessScore = inputs.existingPreparednessScore; 

// Step 1: Calculate Disruption Hazard Factor (DHF) 
// Higher budget cuts, higher vacancies, higher external crisis likelihood increase hazard. 
// Weights adjusted to reflect relative impact: staff shortages and external crises often have immediate, severe impact.
const budgetImpact = Math.max(0, (100 - serviceBudgetRatio) * 0.2); // 0-20 
const staffImpact = Math.min(100, staffVacancyRate * 0.8); // 0-80 
const crisisImpact = externalCrisisIndex * 5; // 5-50 
const disruptionHazardFactor = Math.min(100, (budgetImpact + staffImpact + crisisImpact) / 1.5); // Normalize to approx 0-100 scale

// Step 2: Calculate Community Vulnerability & Service Impact Factor (CVSIF) 
// Higher community vulnerability and more critical services increase potential impact. 
const vulnerabilityWeight = communityVulnerabilityIndex * 6; // 6-60 
const criticalityWeight = serviceCriticalityIndex * 4; // 4-40 
const communityServiceImpactFactor = Math.min(100, (vulnerabilityWeight + criticalityWeight) * 1.0); // Normalize to approx 0-100 scale

// Step 3: Calculate Preparedness & Recovery Capacity Factor (PRCF) 
// Higher resource availability and existing preparedness decrease risk and recovery time. 
const resourceCapacity = recoveryResourceAvailability * 5; // 5-50 
const preparednessStrength = existingPreparednessScore * 5; // 5-50 
const preparednessRecoveryCapacityFactor = Math.min(100, (resourceCapacity + preparednessStrength)); // Normalize to approx 0-100

// Step 4: Calculate Overall Disruption Risk Score (ODRS) 
// Combines hazard, impact, and inverse of capacity. Higher score means higher risk. 
// Hazard and impact are direct contributors; capacity is a mitigating factor (higher capacity reduces risk).
const baseRisk = (disruptionHazardFactor * 0.45) + (communityServiceImpactFactor * 0.4); 
const mitigatedRisk = baseRisk * (1 - (preparednessRecoveryCapacityFactor / 200)); // Divide by 200 to give PRCF more weight. 
let disruptionRiskScore = Math.max(0, Math.min(100, (mitigatedRisk + (disruptionHazardFactor * 0.15)))); // Add a slight boost from hazard to ensure it always contributes.
disruptionRiskScore = Math.round(disruptionRiskScore * 10) / 10;

// Step 5: Estimate Potentially Affected Population 
// A portion of the total population is affected, scaled by risk score and community vulnerability. 
const baseAffectedPopulation = totalCommunityPopulation * (disruptionRiskScore / 100); 
const vulnerabilityAdjustment = (communityVulnerabilityIndex / 10) * 0.5; // Up to 50% increase based on vulnerability 
let estimatedAffectedPopulation = baseAffectedPopulation * (1 + vulnerabilityAdjustment); 
estimatedAffectedPopulation = Math.min(totalCommunityPopulation, Math.round(estimatedAffectedPopulation)); // Cap at total population 

// Step 6: Estimate Recovery Time in Weeks 
// Higher risk, higher criticality, and lower recovery capacity lead to longer recovery times. 
const maxBaseRecoveryWeeks = 52 * 2; // Up to 2 years base 
const riskComponent = disruptionRiskScore / 100; 
const criticalityComponent = serviceCriticalityIndex / 10; 
const recoveryCapacityComponent = Math.max(0.1, (100 - preparednessRecoveryCapacityFactor) / 100); // Ensure no division by zero, min 0.1 for slow recovery

let estimatedRecoveryTimeWeeks = maxBaseRecoveryWeeks * riskComponent * criticalityComponent * recoveryCapacityComponent * 0.8; // Apply a scaling factor to prevent excessively high values
estimatedRecoveryTimeWeeks = Math.max(1, Math.min(maxBaseRecoveryWeeks, estimatedRecoveryTimeWeeks)); // Min 1 week, Max 2 years

return { 
  disruptionRiskScore: disruptionRiskScore, 
  estimatedAffectedPopulation: estimatedAffectedPopulation, 
  estimatedRecoveryTimeWeeks: estimatedRecoveryTimeWeeks 
};
