function calculateInclusivity(inputs) {
  const { 
    antiDiscriminationLaws, 
    diversityInitiativesBudget, 
    minorityRepresentationInLeadership, 
    accessiblePublicTransport, 
    multilingualServicesAvailability, 
    discriminationIncidentsPer10k, 
    communityEngagementDiversity
  } = inputs;

  // Input validation and clamping
  const clamp = (value, min, max) => Math.max(min, Math.min(value, max));

  const adl = clamp(antiDiscriminationLaws, 0, 5);
  const dib = clamp(diversityInitiativesBudget, 0, 10); // Max 10% for budget impact
  const mril = clamp(minorityRepresentationInLeadership, 0, 100);
  const apt = clamp(accessiblePublicTransport, 0, 5);
  const msa = clamp(multilingualServicesAvailability, 0, 5);
  const dipk = Math.max(0, discriminationIncidentsPer10k); // Cannot be negative
  const ced = clamp(communityEngagementDiversity, 0, 5);

  // --- Step 1: Calculate Policy & Governance Score (Weight: 30%) ---
  // antiDiscriminationLaws (0-5) -> (0-100)
  // diversityInitiativesBudget (0-10%) -> (0-100), where 5% is optimal (e.g. 5% budget = 100 points, 0% = 0 points)
  const policyLawScore = (adl / 5) * 100;
  const budgetImpact = Math.min(100, (dib / 5) * 100); // Scale budget: 5% is considered 'excellent' for 100 points
  const policyAndGovernanceScore = (policyLawScore * 0.6 + budgetImpact * 0.4);

  // --- Step 2: Calculate Representation & Engagement Score (Weight: 25%) ---
  // minorityRepresentationInLeadership (0-100% directly maps)
  // communityEngagementDiversity (0-5) -> (0-100)
  const engagementScore = (ced / 5) * 100;
  const representationAndEngagementScore = (mril * 0.7 + engagementScore * 0.3);

  // --- Step 3: Calculate Access to Services Score (Weight: 25%) ---
  // accessiblePublicTransport (0-5) -> (0-100)
  // multilingualServicesAvailability (0-5) -> (0-100)
  const aptScore = (apt / 5) * 100;
  const msaScore = (msa / 5) * 100;
  const accessToServicesScore = (aptScore * 0.5 + msaScore * 0.5);

  // --- Step 4: Calculate Discrimination Impact Factor (Negative Weighting) ---
  // discriminationIncidentsPer10k: Higher value means greater negative impact.
  // Normalize incidents: 0 incidents = 0 penalty, 50 incidents = max penalty.
  const maxIncidentsForPenalty = 50; // Arbitrary cap for max impact
  const rawPenalty = (dipk / maxIncidentsForPenalty); // Goes from 0 to 1+
  const discriminationImpactFactor = Math.min(1, rawPenalty); // Cap at 1 (100% penalty for 50+ incidents)

  // --- Step 5: Aggregate into Final Community Inclusivity Index (0-100) ---
  const combinedPositiveScore = (
    policyAndGovernanceScore * 0.30 + 
    representationAndEngagementScore * 0.25 + 
    accessToServicesScore * 0.25
  ); // Sum of weighted positive aspects (80% of total)

  // The discrimination impact factor negatively scales the combined positive score.
  // If discriminationImpactFactor is 0, no change. If 1, score is reduced significantly.
  // Let's make it reduce the total by up to 20% of the maximum potential score.
  const discriminationPenalty = combinedPositiveScore * discriminationImpactFactor * 0.25; // max 25% reduction of current combined score

  let communityInclusivityIndex = combinedPositiveScore - discriminationPenalty;

  // Ensure final index is between 0 and 100
  communityInclusivityIndex = clamp(communityInclusivityIndex, 0, 100);

  return {
    policyAndGovernanceScore: parseFloat(policyAndGovernanceScore.toFixed(1)),
    representationAndEngagementScore: parseFloat(representationAndEngagementScore.toFixed(1)),
    accessToServicesScore: parseFloat(accessToServicesScore.toFixed(1)),
    discriminationImpactFactor: parseFloat(discriminationImpactFactor.toFixed(2)),
    communityInclusivityIndex: parseFloat(communityInclusivityIndex.toFixed(1))
  };
}
