formula:
const mapImpact = (value) => value - 3;

const mapVulnerabilityMultiplier = (value) => {
    switch (value) {
        case 1: return 0.8;
        case 2: return 0.9;
        case 3: return 1.0;
        case 4: return 1.1;
        case 5: return 1.2;
        default: return 1.0;
    }
};

const mapSupportFactor = (value) => {
    switch (value) {
        case 1: return 0.7;
        case 2: return 0.85;
        case 3: return 1.0;
        case 4: return 1.15;
        case 5: return 1.3;
        default: return 1.0;
    }
};

const mappedAccessImpact = mapImpact(inputs.Access_Impact);
const mappedSafetyImpact = mapImpact(inputs.Safety_Impact);
const mappedSocialAcceptanceImpact = mapImpact(inputs.Social_Acceptance_Impact);

let coreImpactIndex = (mappedAccessImpact + mappedSafetyImpact + mappedSocialAcceptanceImpact) / 3;

const vulnerabilityMultiplier = mapVulnerabilityMultiplier(inputs.Target_Group_Vulnerability);
let vulnerabilityAdjustedImpact = coreImpactIndex * vulnerabilityMultiplier;

const consultationModifier = mapSupportFactor(inputs.Community_Consultation_Level);
const feasibilityModifier = mapSupportFactor(inputs.Implementation_Feasibility);
const infrastructureModifier = mapSupportFactor(inputs.Existing_Infrastructure_Support);

let averageSupportFactor = (consultationModifier + feasibilityModifier + infrastructureModifier) / 3;

let finalRawScore = vulnerabilityAdjustedImpact * averageSupportFactor * 32;

let overallImpactScore = Math.max(-100, Math.min(100, finalRawScore));

let impactCategory;
if (overallImpactScore >= 75) {
    impactCategory = "Highly Positive & Transformative Impact";
} else if (overallImpactScore >= 40) {
    impactCategory = "Moderately Positive Impact";
} else if (overallImpactScore >= 10) {
    impactCategory = "Slightly Positive Impact";
} else if (overallImpactScore > -10) {
    impactCategory = "Neutral or Negligible Impact";
} else if (overallImpactScore > -40) {
    impactCategory = "Slightly Negative Impact";
} else if (overallImpactScore > -75) {
    impactCategory = "Moderately Negative Impact";
} else {
    impactCategory = "Highly Negative & Harmful Impact";
}

return {
    overall_impact_score: overallImpactScore,
    impact_category: impactCategory
};
