function calculate({ populationSize, licensedProfessionals, insuredPercentage, publicFundingPrograms, averageWaitTimeWeeks, outreachEducationPrograms, culturalCompetencyScore, telehealthPenetrationPercentage }) {
  // Input validation and default to 0 for calculations if invalid
  populationSize = Math.max(1, parseFloat(populationSize) || 0); // Ensure population is at least 1 to avoid division by zero
  licensedProfessionals = Math.max(0, parseFloat(licensedProfessionals) || 0);
  insuredPercentage = Math.max(0, Math.min(100, parseFloat(insuredPercentage) || 0));
  publicFundingPrograms = Math.max(0, parseFloat(publicFundingPrograms) || 0);
  averageWaitTimeWeeks = Math.max(0, parseFloat(averageWaitTimeWeeks) || 0);
  outreachEducationPrograms = Math.max(0, parseFloat(outreachEducationPrograms) || 0);
  culturalCompetencyScore = Math.max(1, Math.min(5, parseFloat(culturalCompetencyScore) || 1));
  telehealthPenetrationPercentage = Math.max(0, Math.min(100, parseFloat(telehealthPenetrationPercentage) || 0));

  // 1. Calculate Practitioner Availability Score (PAS)
  // Benchmark: 70 professionals per 100,000 residents for excellent access
  const practitionerRatio = (licensedProfessionals / populationSize) * 100000;
  const PAS = Math.min(100, (practitionerRatio / 70) * 100);

  // 2. Calculate Insurance Coverage Score (ICS)
  // Directly uses the percentage, capped at 100.
  const ICS = insuredPercentage;

  // 3. Calculate Program & Funding Score (PFS)
  // Benchmark: 1 public program per 10,000 residents for excellent coverage
  const programsPer10k = (publicFundingPrograms / populationSize) * 10000;
  const PFS = Math.min(100, (programsPer10k / 1) * 100);

  // 4. Calculate Wait Time Score (WTS)
  // Shorter wait times = higher score. Penalty for longer waits.
  // 1 week = 90, 5 weeks = 50, 10 weeks = 0. Capped at 0-100.
  const WTS = Math.max(0, 100 - (averageWaitTimeWeeks * 10));

  // 5. Calculate Outreach & Education Score (OES)
  // Benchmark: 0.5 outreach initiatives per 10,000 residents
  const outreachPer10k = (outreachEducationPrograms / populationSize) * 10000;
  const OES = Math.min(100, (outreachPer10k / 0.5) * 100);

  // 6. Calculate Cultural Competency Score (CCS)
  // Scale 1-5 to 0-100
  const CCS = ((culturalCompetencyScore - 1) / 4) * 100;

  // 7. Calculate Telehealth Availability Score (TAS)
  // Directly uses the percentage, capped at 100.
  const TAS = telehealthPenetrationPercentage;

  // 8. Calculate Overall Accessibility & Adequacy Score (Weighted Average)
  // Weights: PAS (20%), ICS (15%), PFS (15%), WTS (20%), OES (10%), CCS (10%), TAS (10%)
  const accessibilityScore = (
    (PAS * 0.20) +
    (ICS * 0.15) +
    (PFS * 0.15) +
    (WTS * 0.20) +
    (OES * 0.10) +
    (CCS * 0.10) +
    (TAS * 0.10)
  );

  // 9. Calculate Resource Adequacy Index (Average of key resource presence scores)
  const resourceAdequacyIndex = (PAS + PFS + OES + TAS) / 4;

  return {
    accessibilityScore: Math.max(0, Math.min(100, accessibilityScore)),
    practitionerRatio: practitionerRatio,
    resourceAdequacyIndex: Math.max(0, Math.min(100, resourceAdequacyIndex))
  };
}
