function calculate({ total_population, youth_population_percentage, adult_mental_health_providers_actual, child_adolescent_mental_health_providers_actual, inpatient_psych_beds_actual, annual_outpatient_visits_actual, ideal_adult_provider_ratio_per_100k, ideal_child_provider_ratio_per_100k, ideal_inpatient_bed_ratio_per_100k, ideal_annual_outpatient_visits_per_person_needing_care, estimated_population_needing_services_percentage }) {

  // Input validation and default to 0 if invalid (though 'required' should prevent this for most)
  const pop = total_population || 0;
  const youth_perc = Math.min(100, Math.max(0, youth_population_percentage || 0));
  const adult_providers_actual = adult_mental_health_providers_actual || 0;
  const child_providers_actual = child_adolescent_mental_health_providers_actual || 0;
  const beds_actual = inpatient_psych_beds_actual || 0;
  const outpatient_visits_actual = annual_outpatient_visits_actual || 0;
  const ideal_adult_ratio = ideal_adult_provider_ratio_per_100k || 0;
  const ideal_child_ratio = ideal_child_provider_ratio_per_100k || 0;
  const ideal_bed_ratio = ideal_inpatient_bed_ratio_per_100k || 0;
  const ideal_outpatient_visits_per_person = ideal_annual_outpatient_visits_per_person_needing_care || 0;
  const services_needing_perc = Math.min(100, Math.max(0, estimated_population_needing_services_percentage || 0));

  if (pop === 0) {
      return {
          adult_provider_gap: 0,
          child_provider_gap: 0,
          total_provider_gap: 0,
          inpatient_bed_gap: 0,
          outpatient_visits_gap: 0,
          outpatient_service_gap_percentage: 0,
          overall_resource_deficit_score: 0
      };
  }

  // 1. Calculate Population Demographics
  const youth_population = pop * (youth_perc / 100);
  const adult_population = pop - youth_population;
  const population_needing_services = pop * (services_needing_perc / 100);

  // 2. Calculate Ideal Resource Levels
  const ideal_adult_providers = (adult_population / 100000) * ideal_adult_ratio;
  const ideal_child_providers = (youth_population / 100000) * ideal_child_ratio;
  const ideal_inpatient_beds = (pop / 100000) * ideal_bed_ratio;
  const ideal_annual_outpatient_visits = population_needing_services * ideal_outpatient_visits_per_person;

  // 3. Calculate Gaps (always non-negative)
  const adult_provider_gap = Math.max(0, ideal_adult_providers - adult_providers_actual);
  const child_provider_gap = Math.max(0, ideal_child_providers - child_providers_actual);
  const total_provider_gap = adult_provider_gap + child_provider_gap;
  const inpatient_bed_gap = Math.max(0, ideal_inpatient_beds - beds_actual);
  const outpatient_visits_gap = Math.max(0, ideal_annual_outpatient_visits - outpatient_visits_actual);

  // 4. Calculate Outpatient Service Gap Percentage
  const outpatient_service_gap_percentage = (ideal_annual_outpatient_visits > 0) 
      ? (outpatient_visits_gap / ideal_annual_outpatient_visits) * 100 
      : 0;

  // 5. Calculate Overall Resource Deficit Score (weighted average, capped at 100)
  // Normalize gaps to 0-100 scale based on ideal values. Add 1 to denominator to avoid division by zero.
  const provider_gap_normalized = (ideal_adult_providers + ideal_child_providers > 0) 
      ? (total_provider_gap / (ideal_adult_providers + ideal_child_providers)) * 100 
      : 0;
  const bed_gap_normalized = (ideal_inpatient_beds > 0) 
      ? (inpatient_bed_gap / ideal_inpatient_beds) * 100 
      : 0;
  const outpatient_gap_normalized = outpatient_service_gap_percentage;

  // Apply weights (e.g., providers 40%, beds 30%, outpatient 30%)
  let overall_score = (provider_gap_normalized * 0.40) + 
                      (bed_gap_normalized * 0.30) + 
                      (outpatient_gap_normalized * 0.30);

  overall_score = Math.min(100, Math.max(0, overall_score)); // Cap score between 0 and 100

  return {
    adult_provider_gap: Math.round(adult_provider_gap),
    child_provider_gap: Math.round(child_provider_gap),
    total_provider_gap: Math.round(total_provider_gap),
    inpatient_bed_gap: Math.round(inpatient_bed_gap),
    outpatient_visits_gap: Math.round(outpatient_visits_gap),
    outpatient_service_gap_percentage: parseFloat(outpatient_service_gap_percentage.toFixed(1)),
    overall_resource_deficit_score: parseFloat(overall_score.toFixed(1))
  };
}
