function calculateResilience(inputs) {
  const { socialCohesionScore, mhServiceAccessScore, economicStabilityScore, institutionalTrustScore, historicalTraumaIndex, civicEngagementRate, disasterPreparednessScore, resourceAvailabilityScore } = inputs;

  // Input validation and clamping to ensure scores are within expected ranges
  const validateScore = (score, min, max) => Math.min(Math.max(score, min), max);

  const sc = validateScore(socialCohesionScore, 1, 10);
  const mh = validateScore(mhServiceAccessScore, 1, 10);
  const es = validateScore(economicStabilityScore, 1, 10);
  const it = validateScore(institutionalTrustScore, 1, 10);
  const ht = validateScore(historicalTraumaIndex, 0, 10);
  const ce = validateScore(civicEngagementRate, 0, 100) / 10; // Normalize % to 0-10 scale
  const dp = validateScore(disasterPreparednessScore, 1, 10);
  const ra = validateScore(resourceAvailabilityScore, 1, 10);

  // Step 1: Calculate the foundational resilience score based on weighted averages
  // Weights designed to emphasize direct support and stability, while historical trauma acts as a significant modifier.
  const weightSC = 0.20;
  const weightMH = 0.20;
  const weightES = 0.15;
  const weightIT = 0.10;
  const weightCE = 0.10;
  const weightDP = 0.10;
  const weightRA = 0.15;

  const foundationalResilience = (
    (sc * weightSC) +
    (mh * weightMH) +
    (es * weightES) +
    (it * weightIT) +
    (ce * weightCE) +
    (dp * weightDP) +
    (ra * weightRA)
  ) / (weightSC + weightMH + weightES + weightIT + weightCE + weightDP + weightRA);

  // Step 2: Determine the Historical Trauma Impact Factor
  // The higher the historical trauma index (ht), the greater the reduction in resilience.
  // A trauma index of 0 means no reduction (factor of 1).
  // A trauma index of 10 means a significant reduction (e.g., factor of 0.2, reducing resilience by 80%).
  const maxTraumaReductionFactor = 0.8; // Max 80% reduction from historical trauma
  const historicalTraumaReduction = (ht / 10) * maxTraumaReductionFactor; // Scales from 0 to 0.8
  const traumaImpactFactor = Math.max(0.2, 1 - historicalTraumaReduction); // Cap at minimum 20% of base resilience

  // Step 3: Adjust the foundational resilience score with the Historical Trauma Impact Factor
  let communityResilienceIndex = foundationalResilience * traumaImpactFactor;

  // Ensure the final index is within a sensible range, e.g., 1 to 10
  communityResilienceIndex = Math.min(Math.max(communityResilienceIndex, 1), 10);

  // Step 4: Compute auxiliary scores
  const supportServicesEffectiveness = (mh + ra + dp) / 3; // Average of direct support related scores
  const historicalTraumaImpact = ht; // Directly use the index for impact score

  // Step 5: Categorize overall vulnerability
  let overallVulnerability = '';
  if (communityResilienceIndex >= 8.5) {
    overallVulnerability = 'Very Low Vulnerability';
  } else if (communityResilienceIndex >= 7.0) {
    overallVulnerability = 'Low Vulnerability';
  } else if (communityResilienceIndex >= 5.0) {
    overallVulnerability = 'Moderate Vulnerability';
  } else if (communityResilienceIndex >= 3.0) {
    overallVulnerability = 'High Vulnerability';
  } else {
    overallVulnerability = 'Very High Vulnerability';
  }

  return {
    communityResilienceIndex: communityResilienceIndex,
    historicalTraumaImpact: historicalTraumaImpact,
    supportServicesEffectiveness: supportServicesEffectiveness,
    overallVulnerability: overallVulnerability
  };
}
