function calculate({ community_annual_demand_kwh, peak_load_kw, solar_pv_system_size_kw, battery_capacity_kwh, solar_cost_per_kw, battery_cost_per_kwh, grid_electricity_price_per_kwh, outage_frequency_per_year, avg_outage_duration_hours, cost_of_outage_per_kwh_lost }) {

    // Input validation and default handling
    const demand = Math.max(0, community_annual_demand_kwh);
    const peakLoad = Math.max(0, peak_load_kw);
    const solarSize = Math.max(0, solar_pv_system_size_kw);
    const batteryCap = Math.max(0, battery_capacity_kwh);
    const solarCost = Math.max(0, solar_cost_per_kw);
    const batteryCost = Math.max(0, battery_cost_per_kwh);
    const gridPrice = Math.max(0, grid_electricity_price_per_kwh);
    const outageFreq = Math.max(0, outage_frequency_per_year);
    const outageDur = Math.max(0, avg_outage_duration_hours);
    const costOfOutage = Math.max(0, cost_of_outage_per_kwh_lost);

    // Constants
    const SOLAR_YIELD_KWH_PER_KW_YEAR = 1300; // Average annual kWh generation per kWp installed
    const PROJECT_LIFETIME_YEARS = 25;
    const O_AND_M_PERCENT_OF_CAPEX = 0.015; // 1.5% of capital cost for annual O&M
    const BATTERY_DISCHARGE_EFFICIENCY = 0.9; // 90% discharge efficiency for batteries

    // 1. Total Estimated Capital Cost
    const total_solar_cost = solarSize * solarCost;
    const total_battery_cost = batteryCap * batteryCost;
    const total_project_cost_usd = total_solar_cost + total_battery_cost;

    // 2. Estimated Annual Renewable Generation
    const annual_renewable_generation_kwh = solarSize * SOLAR_YIELD_KWH_PER_KW_YEAR;

    // 3. Annual Grid Import Reduction
    // Assuming microgrid prioritizes serving local demand with renewables first.
    const annual_grid_import_reduction_kwh = Math.min(annual_renewable_generation_kwh, demand);

    // 4. Estimated Blackout Autonomy (hours)
    // How long the battery can supply the peak load
    const autonomy_hours = (peakLoad > 0) ? (batteryCap * BATTERY_DISCHARGE_EFFICIENCY) / peakLoad : Infinity;
    // Cap autonomy at a reasonable maximum, e.g., 72 hours (3 days)
    const capped_autonomy_hours = Math.min(autonomy_hours, 720); // 30 days is a high cap for 'autonomy'

    // 5. Annual Grid Bill Savings
    const annual_grid_bill_savings_usd = annual_grid_import_reduction_kwh * gridPrice;

    // 6. Annual Resilience Value
    // Economic value from avoiding outages. Assume average hourly demand during an outage scenario.
    const avg_hourly_demand_during_outage_kw = (demand > 0 && outageDur > 0) ? (demand / 8760) : peakLoad; // Simpler approximation, or just use peak load as worst case
    const annual_kwh_lost_due_to_outages = outageFreq * outageDur * avg_hourly_demand_during_outage_kw;
    const annual_resilience_value_usd = annual_kwh_lost_due_to_outages * costOfOutage;

    // 7. Total Annual Benefits (Savings + Resilience)
    const total_annual_benefits_usd = annual_grid_bill_savings_usd + annual_resilience_value_usd;

    // 8. Simple Payback Period
    const simple_payback_years = (total_annual_benefits_usd > 0) ? total_project_cost_usd / total_annual_benefits_usd : Infinity;

    // 9. Levelized Cost of Energy (LCOE)
    const annual_om_cost = total_project_cost_usd * O_AND_M_PERCENT_OF_CAPEX;
    const total_annualized_cost = (total_project_cost_usd / PROJECT_LIFETIME_YEARS) + annual_om_cost;
    const lcoe_cents_per_kwh = (annual_renewable_generation_kwh > 0) ? (total_annualized_cost / annual_renewable_generation_kwh) * 100 : Infinity;

    // 10. Feasibility Rating
    let feasibility_rating;
    if (simple_payback_years < 10 && lcoe_cents_per_kwh < (gridPrice * 100 * 0.9) && capped_autonomy_hours >= 24) {
        feasibility_rating = "High";
    } else if (simple_payback_years >= 10 && simple_payback_years <= 20 && lcoe_cents_per_kwh < (gridPrice * 100 * 1.1) && capped_autonomy_hours >= 12) {
        feasibility_rating = "Medium";
    } else if (simple_payback_years > 20 || lcoe_cents_per_kwh >= (gridPrice * 100 * 1.1) || total_project_cost_usd === 0) {
        feasibility_rating = "Low";
    } else {
        feasibility_rating = "Requires More Analysis"; // Catch-all for unusual scenarios
    }

    // Edge cases for outputs
    if (isNaN(simple_payback_years) || !isFinite(simple_payback_years)) simple_payback_years = Infinity;
    if (isNaN(lcoe_cents_per_kwh) || !isFinite(lcoe_cents_per_kwh)) lcoe_cents_per_kwh = Infinity;
    if (total_project_cost_usd === 0 && annual_renewable_generation_kwh === 0) {
        lcoe_cents_per_kwh = 0;
        feasibility_rating = "N/A - No System Proposed";
    } else if (total_project_cost_usd === 0) {
         feasibility_rating = "High - Zero Cost System!";
    }
    if (demand === 0 && solarSize === 0 && batteryCap === 0) {
        feasibility_rating = "No inputs provided";
    }

    return {
        total_project_cost_usd: total_project_cost_usd,
        annual_renewable_generation_kwh: annual_renewable_generation_kwh,
        annual_grid_import_reduction_kwh: annual_grid_import_reduction_kwh,
        autonomy_hours: capped_autonomy_hours,
        simple_payback_years: simple_payback_years,
        lcoe_cents_per_kwh: lcoe_cents_per_kwh,
        annual_resilience_value_usd: annual_resilience_value_usd,
        feasibility_rating: feasibility_rating
    };
}
