function calculateRisk(inputs) {
    const {
        expectedParticipants,
        locationDensity,
        leHistoryScore,
        observerPresence,
        isPermitted,
        recentRegionalConflictScore,
        counterProtestLikelihood,
        protestDurationHours
    } = inputs;

    let overallRiskScoreRaw = 0;
    let leInterventionLikelihoodRaw = 0;
    let participantInjuryRiskRaw = 0;

    // Input validation and clamping (ensure inputs are within expected ranges)
    const clampedParticipants = Math.max(1, expectedParticipants);
    const clampedLocationDensity = Math.max(1, Math.min(5, locationDensity));
    const clampedLeHistoryScore = Math.max(1, Math.min(5, leHistoryScore));
    const clampedObserverPresence = Math.max(0, Math.min(1, observerPresence));
    const clampedIsPermitted = Math.max(0, Math.min(1, isPermitted));
    const clampedRecentRegionalConflictScore = Math.max(1, Math.min(5, recentRegionalConflictScore));
    const clampedCounterProtestLikelihood = Math.max(0, Math.min(1, counterProtestLikelihood));
    const clampedProtestDurationHours = Math.max(1, Math.min(24, protestDurationHours));


    // --- Calculation for Overall Risk Score --- 
    // 1. Participant Count: Higher count generally increases risk, but with diminishing returns.
    if (clampedParticipants < 100) overallRiskScoreRaw += 0.5;
    else if (clampedParticipants <= 500) overallRiskScoreRaw += 1.0;
    else if (clampedParticipants <= 2000) overallRiskScoreRaw += 1.5;
    else if (clampedParticipants <= 10000) overallRiskScoreRaw += 2.0;
    else overallRiskScoreRaw += 2.5; // Capped at 2.5 for very large numbers

    // 2. Location Density (1-5): Denser locations can increase crowding and reduce escape routes.
    overallRiskScoreRaw += clampedLocationDensity * 0.4; // Max +2.0

    // 3. LE History Score (1-5): A history of aggressive tactics significantly elevates risk.
    overallRiskScoreRaw += clampedLeHistoryScore * 0.8; // Max +4.0

    // 4. Observer Presence (0=No, 1=Yes): Lack of independent observers increases risk of unchecked actions.
    if (clampedObserverPresence === 0) overallRiskScoreRaw += 1.0; // Penalty if no observers

    // 5. Is Permitted (0=No, 1=Yes): Unpermitted protests may face higher legal and enforcement risks.
    if (clampedIsPermitted === 0) overallRiskScoreRaw += 0.8; // Penalty if not permitted

    // 6. Recent Regional Conflict Score (1-5): Pre-existing tensions can escalate situations.
    overallRiskScoreRaw += clampedRecentRegionalConflictScore * 0.6; // Max +3.0

    // 7. Counter Protesters (0=Low/None, 1=High Likelihood): Direct confrontation risk.
    if (clampedCounterProtestLikelihood === 1) overallRiskScoreRaw += 2.5; // Significant increase

    // 8. Protest Duration (hours): Longer duration can lead to fatigue, reduced visibility (at night), and increased opportunity for escalation.
    overallRiskScoreRaw += Math.min(clampedProtestDurationHours / 4, 2.0); // Max +2.0 for 8+ hours

    // Normalize Overall Risk Score to 0-10. Max theoretical raw score is around 17.8.
    const overallRiskScore = parseFloat(Math.min(overallRiskScoreRaw / 1.78, 10).toFixed(1));

    // --- Calculation for LE Intervention Likelihood ---
    // 1. Participant Count: Larger numbers can be perceived as more challenging to manage.
    if (clampedParticipants < 500) leInterventionLikelihoodRaw += 0.5;
    else if (clampedParticipants <= 2000) leInterventionLikelihoodRaw += 1.0;
    else if (clampedParticipants <= 10000) leInterventionLikelihoodRaw += 1.5;
    else leInterventionLikelihoodRaw += 2.0;

    // 2. LE History Score: Directly proportional to likelihood of intervention.
    leInterventionLikelihoodRaw += clampedLeHistoryScore * 1.0; // Max +5.0

    // 3. Is Permitted: Unpermitted events are inherently more likely to face intervention.
    if (clampedIsPermitted === 0) leInterventionLikelihoodRaw += 2.0; // Strong increase

    // 4. Recent Regional Conflict Score: Heightened tensions can lead to more proactive LE presence.
    leInterventionLikelihoodRaw += clampedRecentRegionalConflictScore * 0.5; // Max +2.5

    // 5. Protest Duration: Longer protests provide more time for situations to develop and for LE to mobilize/intervene.
    leInterventionLikelihoodRaw += Math.min(clampedProtestDurationHours / 3, 1.5); // Max +1.5 for 4.5+ hours

    // 6. Location Density: Denser locations might see more intervention due to public space management concerns.
    leInterventionLikelihoodRaw += clampedLocationDensity * 0.3; // Max +1.5

    // Normalize LE Intervention Likelihood to 0-10. Max theoretical raw score is around 14.5.
    const leInterventionLikelihood = parseFloat(Math.min(leInterventionLikelihoodRaw / 1.45, 10).toFixed(1));

    // --- Calculation for Participant Injury Risk ---
    // 1. Overall Risk Score (indirect influence): A higher general risk naturally translates to higher injury potential.
    participantInjuryRiskRaw += overallRiskScore * 0.7; // Max 7.0 (if overallRiskScore is 10)

    // 2. Counter Protesters: Direct physical conflict significantly increases injury risk.
    if (clampedCounterProtestLikelihood === 1) participantInjuryRiskRaw += 3.0;

    // 3. LE History Score: Aggressive LE tactics can directly cause injuries.
    participantInjuryRiskRaw += clampedLeHistoryScore * 0.6; // Max +3.0

    // 4. Location Density: Crowding in dense areas can lead to trampling, difficulty for first aid.
    participantInjuryRiskRaw += clampedLocationDensity * 0.4; // Max +2.0

    // 5. Protest Duration: Prolonged exposure increases chances of accidental injury or fatigue-related incidents.
    participantInjuryRiskRaw += Math.min(clampedProtestDurationHours / 5, 1.0); // Max +1.0 for 5+ hours

    // 6. Observer Presence: Lack of observers may make accountability difficult and thus indirectly increase risk of harm.
    if (clampedObserverPresence === 0) participantInjuryRiskRaw += 0.5; // Minor penalty

    // Normalize Participant Injury Risk to 0-10. Max theoretical raw score is around 16.5.
    const participantInjuryRisk = parseFloat(Math.min(participantInjuryRiskRaw / 1.65, 10).toFixed(1));

    return {
        overallRiskScore,
        leInterventionLikelihood,
        participantInjuryRisk
    };
}
