function calculate({ totalTrainingBudget, communityPopulation, highRiskPopulationPercentage, annualSafetyIncidents, basicTrainingCostPerPerson, advancedTrainingCostPerPerson, desiredBasicTrainingReachPercentage, existingPartnershipEfficiencyFactor }) {

  // Input Validation and Defaults
  totalTrainingBudget = Math.max(0, totalTrainingBudget || 0);
  communityPopulation = Math.max(1, communityPopulation || 1); // Avoid division by zero in calculations that use population
  highRiskPopulationPercentage = Math.min(100, Math.max(0, highRiskPopulationPercentage || 0));
  annualSafetyIncidents = Math.max(0, annualSafetyIncidents || 0);
  basicTrainingCostPerPerson = Math.max(0.01, basicTrainingCostPerPerson || 0.01); // Min cost to prevent division by zero
  advancedTrainingCostPerPerson = Math.max(0.01, advancedTrainingCostPerPerson || 0.01); // Min cost
  desiredBasicTrainingReachPercentage = Math.min(100, Math.max(0, desiredBasicTrainingReachPercentage || 0));
  existingPartnershipEfficiencyFactor = Math.min(2.0, Math.max(0.5, existingPartnershipEfficiencyFactor || 1.0));

  let outputs = {
    basicTrainingBudget: 0,
    advancedTrainingBudget: 0,
    contingencyFund: 0,
    estimatedBasicParticipants: 0,
    estimatedAdvancedParticipants: 0,
    overallTrainingReachPercentage: 0,
    budgetUtilizationRate: 0
  };

  if (totalTrainingBudget === 0) {
    return outputs;
  }

  // --- Step 1: Calculate Base Needs and Risk-Based Allocation Factors --- 

  // Target basic participants based on desired reach
  const targetBasicParticipants = communityPopulation * (desiredBasicTrainingReachPercentage / 100);

  // Preliminary basic training cost, adjusted by efficiency factor
  // A factor > 1 means efficiency, so cost is reduced (division).
  // A factor < 1 means inefficiency, so cost is increased.
  let preliminaryBasicTrainingCost = targetBasicParticipants * basicTrainingCostPerPerson / existingPartnershipEfficiencyFactor;

  // Determine an 'urgency' or 'need' score for advanced training based on high-risk population and incidents.
  // This score will influence the proportion of the budget allocated to advanced training.
  // Higher risk % and more incidents lead to a higher multiplier for advanced training allocation.
  // Incident rate per 1000 people for better scaling.
  const incidentRatePer1000 = (communityPopulation > 0) ? (annualSafetyIncidents / communityPopulation) * 1000 : 0;

  // Base multiplier to scale advanced training allocation
  const riskIncidentMultiplier = 1 + (highRiskPopulationPercentage / 100 * 0.5) + (incidentRatePer1000 / 100 * 0.5); 

  // Cap this multiplier to prevent extreme skewing, e.g., max 2x base advanced allocation
  const cappedRiskIncidentMultiplier = Math.min(2.0, Math.max(1.0, riskIncidentMultiplier));

  // Initial allocation percentage for advanced training (e.g., 8-20% of the *total budget* before contingency, influenced by risk)
  // Base advanced allocation as a percentage of total budget (e.g., 8%), then adjust with multiplier
  let advancedAllocationPercentage = 0.08 * cappedRiskIncidentMultiplier;
  // Ensure advanced allocation doesn't exceed a reasonable cap, e.g., 25% of total budget initially
  advancedAllocationPercentage = Math.min(0.25, advancedAllocationPercentage);

  // --- Step 2: Budget Allocation Prioritization --- 

  let remainingBudget = totalTrainingBudget;

  // Allocate for Contingency First (as a percentage of total budget, ensuring it's not too high)
  const targetContingencyPercentage = 0.07; // 7% of total budget as a target
  let calculatedContingency = totalTrainingBudget * targetContingencyPercentage;
  outputs.contingencyFund = Math.min(remainingBudget * 0.20, calculatedContingency); // Max 20% of remaining budget for contingency at this stage
  remainingBudget -= outputs.contingencyFund;

  // Allocate for Basic Training (Priority 1)
  // Try to meet the desired basic training cost, but cap it to ensure other needs can also be met
  outputs.basicTrainingBudget = Math.min(remainingBudget * 0.80, preliminaryBasicTrainingCost); // Max 80% of remaining budget for basic
  remainingBudget -= outputs.basicTrainingBudget;

  // Allocate for Advanced Training (Priority 2)
  // Try to meet the risk-based advanced allocation percentage, using remaining funds
  let proposedAdvancedBudget = totalTrainingBudget * advancedAllocationPercentage;
  outputs.advancedTrainingBudget = Math.min(remainingBudget, proposedAdvancedBudget);
  remainingBudget -= outputs.advancedTrainingBudget;

  // Re-allocate any surplus to contingency
  outputs.contingencyFund += remainingBudget;
  remainingBudget = 0; // Budget fully allocated

  // --- Step 3: Calculate Participants and Reach --- 

  // Participants are estimated based on allocated budget and adjusted cost per person
  outputs.estimatedBasicParticipants = (outputs.basicTrainingBudget > 0 && basicTrainingCostPerPerson > 0) ? 
    Math.floor(outputs.basicTrainingBudget / (basicTrainingCostPerPerson / existingPartnershipEfficiencyFactor)) : 0;

  outputs.estimatedAdvancedParticipants = (outputs.advancedTrainingBudget > 0 && advancedTrainingCostPerPerson > 0) ? 
    Math.floor(outputs.advancedTrainingBudget / (advancedTrainingCostPerPerson / existingPartnershipEfficiencyFactor)) : 0;

  // Overall reach for basic training
  outputs.overallTrainingReachPercentage = (communityPopulation > 0) ? 
    Math.min(100, (outputs.estimatedBasicParticipants / communityPopulation) * 100) : 0;

  // Budget utilization rate
  const totalAllocated = outputs.basicTrainingBudget + outputs.advancedTrainingBudget + outputs.contingencyFund;
  outputs.budgetUtilizationRate = (totalTrainingBudget > 0) ? 
    Math.min(100, (totalAllocated / totalTrainingBudget) * 100) : 0;

  // Final adjustment to ensure total allocated funds do not exceed total budget due to cumulative rounding/min/max operations
  // This step addresses potential floating point inaccuracies if any remain.
  if (totalAllocated > totalTrainingBudget + 0.01) { // Allow for tiny floating point errors
      const excess = totalAllocated - totalTrainingBudget;
      outputs.contingencyFund = Math.max(0, outputs.contingencyFund - excess); // Deduct from contingency first
      // Re-calculate totalAllocated for final budget utilization rate with adjustment
      const finalTotalAdjusted = outputs.basicTrainingBudget + outputs.advancedTrainingBudget + outputs.contingencyFund;
      outputs.budgetUtilizationRate = (totalTrainingBudget > 0) ? 
          Math.min(100, (finalTotalAdjusted / totalTrainingBudget) * 100) : 0;
  }

  return outputs;
}
