if (numParticipants <= 0 || costPerParticipant < 0 || avgPreTrainingWage < 0 || expectedWageIncreasePercent < 0 || preTrainingEmploymentRate < 0 || postTrainingEmploymentRate < 0 || lifespanOfBenefitYears <= 0 || communityMultiplier < 1 || averageTaxRate < 0) { return { totalProgramCost: 0, totalAnnualWageIncrease: 0, totalAggregateWageIncreaseOverLifespan: 0, averageIndividualAnnualWageIncrease: 0, totalEconomicImpact: 0, roiRatio: 0, estimatedTaxRevenueIncrease: 0 }; }

const preEmpRate = Math.min(100, Math.max(0, preTrainingEmploymentRate)) / 100;
const postEmpRate = Math.min(100, Math.max(0, postTrainingEmploymentRate)) / 100;
const wageIncreaseFactor = 1 + (expectedWageIncreasePercent / 100);
const taxRateFactor = averageTaxRate / 100;

// 1. Total Program Cost
const totalProgramCost = numParticipants * costPerParticipant;

// 2. Average Post-Training Wage for Employed Individuals
const avgPostTrainingWage = avgPreTrainingWage * wageIncreaseFactor;

// 3. Total Annual Wage for the Group BEFORE Training
const totalAnnualWagePreTraining = numParticipants * preEmpRate * avgPreTrainingWage;

// 4. Total Annual Wage for the Group AFTER Training
const totalAnnualWagePostTraining = numParticipants * postEmpRate * avgPostTrainingWage;

// 5. Aggregate Annual Wage Increase for all participants (can be zero if post-training outcomes are not better)
const totalAnnualWageIncrease = Math.max(0, totalAnnualWagePostTraining - totalAnnualWagePreTraining);

// 6. Total Aggregate Wage Increase Over the Projected Lifespan
const totalAggregateWageIncreaseOverLifespan = totalAnnualWageIncrease * lifespanOfBenefitYears;

// 7. Average Individual Annual Wage Increase
const averageIndividualAnnualWageIncrease = totalAnnualWageIncrease / numParticipants;

// 8. Total Estimated Community Economic Impact (using the multiplier)
const totalEconomicImpact = totalAggregateWageIncreaseOverLifespan * communityMultiplier;

// 9. Return on Investment (ROI) Ratio
const roiRatio = totalProgramCost > 0 ? totalAggregateWageIncreaseOverLifespan / totalProgramCost : 0;

// 10. Estimated Tax Revenue Increase from increased earnings
const estimatedTaxRevenueIncrease = totalAggregateWageIncreaseOverLifespan * taxRateFactor;

return {
    totalProgramCost,
    totalAnnualWageIncrease,
    totalAggregateWageIncreaseOverLifespan,
    averageIndividualAnnualWageIncrease,
    totalEconomicImpact,
    roiRatio,
    estimatedTaxRevenueIncrease
};
