function calculate({ volunteerRate, volunteerHoursPerCapita, mutualAidGroupsPer10k, mutualAidParticipationRate, resourceSharingPlatforms, localResourceAccessibilityScore, communityEventParticipation, socialCapitalScore }) {
    // Input validation and clamping (ensuring values are within expected ranges)
    volunteerRate = Math.max(0, Math.min(100, volunteerRate));
    volunteerHoursPerCapita = Math.max(0, Math.min(100, volunteerHoursPerCapita)); 
    mutualAidGroupsPer10k = Math.max(0, Math.min(20, mutualAidGroupsPer10k));
    mutualAidParticipationRate = Math.max(0, Math.min(100, mutualAidParticipationRate));
    resourceSharingPlatforms = Math.max(0, Math.min(50, resourceSharingPlatforms));
    localResourceAccessibilityScore = Math.max(1, Math.min(10, localResourceAccessibilityScore)); 
    communityEventParticipation = Math.max(0, Math.min(100, communityEventParticipation));
    socialCapitalScore = Math.max(1, Math.min(10, socialCapitalScore)); 

    // --- Normalization (to a 0-1 scale) ---
    const normVolunteerRate = volunteerRate / 100;
    const normVolunteerHoursPerCapita = volunteerHoursPerCapita / 100;
    const normMutualAidGroupsPer10k = mutualAidGroupsPer10k / 20;
    const normMutualAidParticipationRate = mutualAidParticipationRate / 100;
    const normResourceSharingPlatforms = resourceSharingPlatforms / 50;
    const normLocalResourceAccessibilityScore = localResourceAccessibilityScore / 10;
    const normCommunityEventParticipation = communityEventParticipation / 100;
    const normSocialCapitalScore = socialCapitalScore / 10;

    // --- Weighted Sum Calculation ---
    // Weights are designed to reflect relative importance, summing to 1.0 overall.
    // Category 1: Volunteerism (30% of total score)
    const volunteerismFactor = (normVolunteerRate * 0.4 + normVolunteerHoursPerCapita * 0.6) * 0.3;

    // Category 2: Mutual Aid (30% of total score)
    const mutualAidFactor = (normMutualAidGroupsPer10k * 0.4 + normMutualAidParticipationRate * 0.6) * 0.3;

    // Category 3: Resource Sharing (25% of total score)
    const resourceSharingFactor = (normResourceSharingPlatforms * 0.5 + normLocalResourceAccessibilityScore * 0.5) * 0.25;

    // Category 4: Cohesion & Trust (15% of total score)
    const cohesionFactor = (normCommunityEventParticipation * 0.5 + normSocialCapitalScore * 0.5) * 0.15;

    let networkStrengthScore = (volunteerismFactor + mutualAidFactor + resourceSharingFactor + cohesionFactor) * 100;

    // Final score clamping to ensure it's within 0-100 range
    networkStrengthScore = Math.max(0, Math.min(100, networkStrengthScore));

    // --- Categorization based on score ---
    let networkCategory;
    if (networkStrengthScore >= 80) {
        networkCategory = "Exceptional Strength";
    } else if (networkStrengthScore >= 60) {
        networkCategory = "Strong & Resilient";
    } else if (networkStrengthScore >= 40) {
        networkCategory = "Moderate but Developing";
    } else if (networkStrengthScore >= 20) {
        networkCategory = "Emergent Potential";
    } else {
        networkCategory = "Foundational Stage";
    }

    return {
        networkStrengthScore: parseFloat(networkStrengthScore.toFixed(2)),
        networkCategory: networkCategory
    };
}
