const { positiveCitizenInteractions, negativeCitizenInteractions, reportedIncidentsMisconductRate, transparencyScore, publicSentimentIndex, citizenComplaintResolutionRate, communityEngagementEffectiveness } = inputs;

// Input Validation and Defaults
const posInteractions = Math.max(0, positiveCitizenInteractions || 0);
const negInteractions = Math.max(0, negativeCitizenInteractions || 0);
const misconductRate = Math.max(0, reportedIncidentsMisconductRate || 0);
const transparency = Math.max(1, Math.min(10, transparencyScore || 1));
const sentiment = Math.max(0, Math.min(100, publicSentimentIndex || 0));
const resolutionRate = Math.max(0, Math.min(100, citizenComplaintResolutionRate || 0));
const engagement = Math.max(1, Math.min(5, communityEngagementEffectiveness || 1));

// Weights for each factor (sum to 1)
const weights = {
  interactionBalance: 0.20,
  misconduct: 0.25,
  transparency: 0.15,
  sentiment: 0.20,
  resolution: 0.10,
  engagement: 0.10
};

// 1. Normalize Citizen Interaction Balance (scaled to 0-1)
const totalInteractions = posInteractions + negInteractions;
let interactionBalanceNormalized = 0;
if (totalInteractions > 0) {
  interactionBalanceNormalized = (posInteractions - negInteractions) / totalInteractions;
  interactionBalanceNormalized = (interactionBalanceNormalized + 1) / 2; // Scale from [-1, 1] to [0, 1]
} else { // No interactions, assume neutral
  interactionBalanceNormalized = 0.5;
}

// 2. Normalize Misconduct Incident Rate (higher rate = lower trust, scaled to 0-1)
// Assume a 'max acceptable' rate, e.g., 2 incidents per 1000 residents, beyond which trust is severely impacted.
const maxMisconductRate = 2.0; // This can be adjusted based on context/benchmark
const misconductImpact = Math.min(1, misconductRate / maxMisconductRate);
const misconductNormalized = 1 - misconductImpact; // Invert: 1 means 0 incidents, 0 means maxMisconductRate or more

// 3. Normalize Perceived Transparency Score (scaled to 0-1)
const transparencyNormalized = transparency / 10;

// 4. Normalize Public Sentiment Index (scaled to 0-1)
const sentimentNormalized = sentiment / 100;

// 5. Normalize Citizen Complaint Resolution Rate (scaled to 0-1)
const resolutionNormalized = resolutionRate / 100;

// 6. Normalize Community Engagement Program Effectiveness (scaled to 0-1)
const engagementNormalized = (engagement - 1) / 4; // Scale from [1, 5] to [0, 1]

// Calculate the weighted sum
let trustIndexScore = (
  interactionBalanceNormalized * weights.interactionBalance +
  misconductNormalized * weights.misconduct +
  transparencyNormalized * weights.transparency +
  sentimentNormalized * weights.sentiment +
  resolutionNormalized * weights.resolution +
  engagementNormalized * weights.engagement
);

// Scale the final score to 0-100
trustIndexScore = trustIndexScore * 100;

// Cap the score within 0-100 bounds
trustIndexScore = Math.max(0, Math.min(100, trustIndexScore));

// Determine Trust Level
let trustLevel;
if (trustIndexScore >= 80) {
  trustLevel = "High Trust";
} else if (trustIndexScore >= 60) {
  trustLevel = "Moderate Trust";
} else if (trustIndexScore >= 40) {
  trustLevel = "Low Trust";
} else {
  trustLevel = "Very Low Trust";
}

return { trustIndexScore: trustIndexScore, trustLevel: trustLevel };
