const FOOD_WASTE_CO2E_FACTOR_KG = 0.69; // kg CO2e per kg food waste avoided from landfill (EPA WARM) 
const YARD_WASTE_CO2E_FACTOR_KG = 0.04; // kg CO2e per kg yard waste avoided from landfill (EPA WARM) 
const CAR_CO2E_PER_MILE = 0.4; // Average kg CO2e per mile for a passenger vehicle 
const WEEKS_PER_YEAR = 52; 

let weeklyOrganicWasteKg = inputs.weeklyOrganicWasteKg || 0; 
let compostEfficiency = (inputs.compostEfficiency || 0) / 100; 
let foodWastePercentage = (inputs.foodWastePercentage || 0) / 100; 
let yardWastePercentage = (inputs.yardWastePercentage || 0) / 100; 
let landfillDiversionRate = (inputs.landfillDiversionRate || 0) / 100; 

// Ensure all percentages are within valid range [0, 1] 
weeklyOrganicWasteKg = Math.max(0, weeklyOrganicWasteKg); 
compostEfficiency = Math.min(1, Math.max(0, compostEfficiency)); 
foodWastePercentage = Math.min(1, Math.max(0, foodWastePercentage)); 
yardWastePercentage = Math.min(1, Math.max(0, yardWastePercentage)); 
landfillDiversionRate = Math.min(1, Math.max(0, landfillDiversionRate)); 

// Cap total waste percentage if it exceeds 100% to avoid overcalculation 
const totalInputPercentage = foodWastePercentage + yardWastePercentage; 
if (totalInputPercentage > 1) { 
  const factor = 1 / totalInputPercentage; 
  foodWastePercentage *= factor; 
  yardWastePercentage *= factor; 
} 

// Calculate total annual organic waste diverted from landfill 
const annualWasteDivertedKg = weeklyOrganicWasteKg * WEEKS_PER_YEAR * landfillDiversionRate; 

// Calculate CO2e reduction from food waste 
const foodWasteDivertedKg = annualWasteDivertedKg * foodWastePercentage; 
const co2eReducedFood = foodWasteDivertedKg * FOOD_WASTE_CO2E_FACTOR_KG; 

// Calculate CO2e reduction from yard waste 
const yardWasteDivertedKg = annualWasteDivertedKg * yardWastePercentage; 
const co2eReducedYard = yardWasteDivertedKg * YARD_WASTE_CO2E_FACTOR_KG; 

// Total reduction, adjusted by composting efficiency (not all waste fully breaks down/is managed efficiently) 
const totalCo2eReducedKgYear = (co2eReducedFood + co2eReducedYard) * compostEfficiency; 

const totalCo2eReducedTonsYear = totalCo2eReducedKgYear / 1000; 

const equivalentCarMiles = totalCo2eReducedKgYear / CAR_CO2E_PER_MILE; 

return { 
  totalCo2eReducedKgYear: totalCo2eReducedKgYear, 
  totalCo2eReducedTonsYear: totalCo2eReducedTonsYear, 
  equivalentCarMiles: equivalentCarMiles 
};
