const GRASS_CN = 20;
const KITCHEN_CN = 18;
const LEAVES_CN = 60;
const CARDBOARD_CN = 175;
const WOOD_CHIPS_CN = 400;

const grass_weight = parseFloat(inputs.grass_weight) || 0;
const kitchen_weight = parseFloat(inputs.kitchen_weight) || 0;
const leaves_weight = parseFloat(inputs.leaves_weight) || 0;
const cardboard_weight = parseFloat(inputs.cardboard_weight) || 0;
const wood_chips_weight = parseFloat(inputs.wood_chips_weight) || 0;
const other_weight = parseFloat(inputs.other_weight) || 0;
const other_cn_ratio = parseFloat(inputs.other_cn_ratio) || 0;

let totalCarbonParts = 0;
let totalNitrogenParts = 0;

if (grass_weight > 0) {
    totalCarbonParts += grass_weight * GRASS_CN;
    totalNitrogenParts += grass_weight;
}
if (kitchen_weight > 0) {
    totalCarbonParts += kitchen_weight * KITCHEN_CN;
    totalNitrogenParts += kitchen_weight;
}
if (leaves_weight > 0) {
    totalCarbonParts += leaves_weight * LEAVES_CN;
    totalNitrogenParts += leaves_weight;
}
if (cardboard_weight > 0) {
    totalCarbonParts += cardboard_weight * CARDBOARD_CN;
    totalNitrogenParts += cardboard_weight;
}
if (wood_chips_weight > 0) {
    totalCarbonParts += wood_chips_weight * WOOD_CHIPS_CN;
    totalNitrogenParts += wood_chips_weight;
}

if (other_weight > 0 && other_cn_ratio > 0) {
    totalCarbonParts += other_weight * other_cn_ratio;
    totalNitrogenParts += other_weight;
}

let combined_cn_ratio = 0;
if (totalNitrogenParts > 0) {
    combined_cn_ratio = totalCarbonParts / totalNitrogenParts;
}

let recommendation = "";
const OPTIMAL_MIN = 25;
const OPTIMAL_MAX = 30;

if (totalNitrogenParts === 0 && totalCarbonParts === 0) {
    recommendation = "Enter material weights to calculate the compost ratio.";
    combined_cn_ratio = 0;
} else if (totalNitrogenParts === 0 && totalCarbonParts > 0) {
    recommendation = "You have input only carbon-rich materials without any nitrogen. Add some 'greens' to start decomposition and prevent a very slow pile.";
    combined_cn_ratio = Infinity; 
} else if (combined_cn_ratio < OPTIMAL_MIN) {
    recommendation = `Your C:N ratio of ${combined_cn_ratio.toFixed(1)} is too low (nitrogen-heavy). Add more 'browns' like dry leaves, wood chips, or shredded paper to balance the ratio.`;
} else if (combined_cn_ratio > OPTIMAL_MAX) {
    recommendation = `Your C:N ratio of ${combined_cn_ratio.toFixed(1)} is too high (carbon-heavy). Add more 'greens' like fresh grass clippings or kitchen scraps to speed up decomposition.`;
} else {
    recommendation = `Your C:N ratio of ${combined_cn_ratio.toFixed(1)} is ideal! Your compost pile should decompose efficiently and produce high-quality humus.`;
}

return {
    total_carbon: totalCarbonParts,
    total_nitrogen: totalNitrogenParts,
    combined_cn_ratio: combined_cn_ratio,
    recommendation: recommendation
};
