const r = annualRate / 100;
const n_comp = compoundingFrequency;
const t = investmentPeriod;
const P = initialPrincipal;
const PMT = regularContribution;
const n_contrib = contributionFrequency;

// Future Value of Initial Principal
const fvPrincipal = P * Math.pow(1 + r / n_comp, n_comp * t);

// Future Value of Regular Contributions (Annuity)
let fvContributions = 0;
const totalRegularContributionsMade = PMT * n_contrib * t;

if (PMT > 0 && totalRegularContributionsMade > 0) {
    // Calculate effective rate per contribution period
    const effectiveRatePerContribPeriod = Math.pow(1 + r / n_comp, n_comp / n_contrib) - 1;
    const numContribPeriods = n_contrib * t;

    if (effectiveRatePerContribPeriod === 0) { // Handle zero interest rate scenario
        fvContributions = totalRegularContributionsMade;
    } else {
        // Annuity Future Value formula (assuming contributions at end of period)
        fvContributions = PMT * (Math.pow(1 + effectiveRatePerContribPeriod, numContribPeriods) - 1) / effectiveRatePerContribPeriod;
    }
}

const futureValue = fvPrincipal + fvContributions;
const totalCapitalInvested = initialPrincipal + totalRegularContributionsMade;
const totalInterest = futureValue - totalCapitalInvested;

return {
    futureValue: futureValue,
    totalInterest: totalInterest,
    totalPeriodicContributions: totalRegularContributionsMade,
    totalCapitalInvested: totalCapitalInvested
};
