const { initialInvestment, monthlyContribution, interestRate, years, compoundsPerYear } = inputs;

const P = parseFloat(initialInvestment) || 0;
const PMT = parseFloat(monthlyContribution) || 0;
const r = (parseFloat(interestRate) || 0) / 100;
const t = parseFloat(years) || 0;

// Parse frequency
let n = 1;
if (compoundsPerYear.includes('Monthly')) n = 12;
else if (compoundsPerYear.includes('Quarterly')) n = 4;
else if (compoundsPerYear.includes('Daily')) n = 365;

if (t <= 0) {
  return {
    futureValue: P,
    totalContributions: P,
    totalInterest: 0
  };
}

// Future Value of Principal: P(1 + r/n)^(nt)
const fvPrincipal = P * Math.pow(1 + r/n, n*t);

// Future Value of a Series (Contributions)
// Formula: PMT * [ (1 + r/n)^(nt) - 1 ] / (r/n)
// Note: This assumes contributions align with compounding period. 
// If Monthly contribs but Annual compounding, this is an approximation.
// For precise 'monthly' contributions regardless of compounding:
// We will treat n as 12 for the contributions part if 'Monthly' is selected, strictly speaking.
// But standard simple tools often just align them. Let's align n to 12 for the contribution part to be safe if inputs imply monthly.

const totalMonths = t * 12;
const totalContributions = P + (PMT * totalMonths);

let fvSeries = 0;
if (r === 0) {
    fvSeries = PMT * totalMonths;
} else {
    // We use a simplified Monthly compounding formula for the contributions to avoid complexity mismatch
    // FV = PMT * ( (1+r/12)^(12t) - 1 ) / (r/12)
    fvSeries = PMT * (Math.pow(1 + r/12, 12*t) - 1) / (r/12);
}

// If the user selected 'Daily' for principal, we keep that for principal, but usually contribs are monthly.
// To be most accurate for this specific tool's UX:
const futureValue = fvPrincipal + fvSeries;
// Note: This calculates Principal compounding at user-selected rate, but contributions compounding Monthly.
// This is a common hybrid approach for personal finance tools.

return {
  futureValue,
  totalContributions,
  totalInterest: futureValue - totalContributions
};
