const annualInterestRateDecimal = interestRate / 100;

let balance = initialInvestment;
let totalContributionsMade = 0;
let totalWithdrawalsMade = 0;
let totalInterestEarned = 0;

const totalMonths = investmentPeriod * 12;

// Pre-calculate per-period amounts based on their frequency
// If frequency is 0, it means no contributions/withdrawals/compounding, so amount is 0 or treated as 'never'.
const contributionPerPeriod = annualContribution / (contributionFrequency || 1);
const withdrawalPerPeriod = annualWithdrawal / (withdrawalFrequency || 1);
const interestRatePerCompoundingPeriod = annualInterestRateDecimal / (compoundingFrequency || 1);

// Track month indices when an event should occur, using Infinity if frequency is 0
const monthsBetweenContributions = contributionFrequency ? Math.round(12 / contributionFrequency) : Infinity;
const monthsBetweenWithdrawals = withdrawalFrequency ? Math.round(12 / withdrawalFrequency) : Infinity;
const monthsBetweenCompoundings = compoundingFrequency ? Math.round(12 / compoundingFrequency) : Infinity;

for (let currentMonth = 0; currentMonth < totalMonths; currentMonth++) {
    // 1. Apply Contributions at the beginning of the month if it's a contribution month
    if (monthsBetweenContributions !== Infinity && currentMonth % monthsBetweenContributions === 0) {
        balance += contributionPerPeriod;
        totalContributionsMade += contributionPerPeriod;
    }

    // 2. Apply Interest if it's a compounding month (at the end of the compounding period)
    // Interest is calculated on the balance *before* withdrawals but *after* contributions for the current period.
    if (monthsBetweenCompoundings !== Infinity && compoundingFrequency > 0 && (currentMonth + 1) % monthsBetweenCompoundings === 0) {
        const interestAdded = balance * interestRatePerCompoundingPeriod;
        balance += interestAdded;
        totalInterestEarned += interestAdded;
    }

    // 3. Apply Withdrawals at the end of the month if it's a withdrawal month
    if (monthsBetweenWithdrawals !== Infinity && currentMonth % monthsBetweenWithdrawals === 0) {
        // Ensure balance does not drop below zero due to withdrawal
        const actualWithdrawal = Math.min(balance, withdrawalPerPeriod);
        balance -= actualWithdrawal;
        totalWithdrawalsMade += actualWithdrawal;
    }

    // Ensure balance does not go negative at any point due to a series of operations in a month
    balance = Math.max(0, balance);
}

return {
    finalBalance: balance,
    totalContributionsMade: totalContributionsMade,
    totalWithdrawalsMade: totalWithdrawalsMade,
    totalInterestEarned: totalInterestEarned
};
