if (inputs.cement_content <= 0) {
    throw new Error("Cement content must be greater than zero.");
}

let water_cement_ratio = inputs.water_content / inputs.cement_content;

// Cap W/C ratio to a realistic and calculable range for concrete strength
// Too low W/C (~0.25) can lead to unworkable mix and incomplete hydration
// Too high W/C (~0.8+) significantly compromises strength
let capped_wcr = Math.max(0.3, Math.min(0.7, water_cement_ratio));

// Base strength estimation based on capped W/C ratio (simplified Abrams' Law inspired)
// This model suggests higher strength for lower W/C ratio.
// Constants are tuned for typical general purpose concrete (e.g., 25-50 MPa range at 28 days, 20°C).
let base_strength_at_28d_20C = Math.max(10, 80 - 85 * capped_wcr);

// Factor for cement type (e.g., High Early Strength cement > 1.0, Low Heat cement < 1.0)
let cement_type_modifier = Math.max(0.7, Math.min(1.3, inputs.cement_type_factor));

// Factor for curing duration (strength gain over time)
// A common empirical model: significant gain in early days, then slows down, plateaus around 28-90 days.
let duration_days = Math.max(1, Math.min(inputs.curing_duration_days, 90)); // Cap duration for calculation realism
let curing_duration_factor = 1.0;
if (duration_days <= 28) {
    // A simplified model for strength gain up to 28 days: starts lower, increases with sqrt(time)
    // At 1 day, factor is ~0.19. At 7 days, factor is ~0.53. At 28 days, factor is 1.0.
    curing_duration_factor = 0.1 + 0.9 * Math.sqrt(duration_days / 28);
} else {
    // After 28 days, strength continues to increase but at a much slower rate.
    // We model a slight additional gain up to 90 days, then plateau.
    curing_duration_factor = 1.0 + 0.05 * Math.min(1, (duration_days - 28) / 62); // ~5% additional strength over next ~60 days
}
curing_duration_factor = Math.min(1.1, curing_duration_factor); // Cap max factor for long-term gain

// Factor for curing temperature
// Higher temperatures generally accelerate early strength gain but can slightly reduce ultimate strength.
// Lower temperatures slow down strength gain.
let temp_c = inputs.curing_temperature_celsius;
let temperature_factor = Math.max(0.7, Math.min(1.15, 1.0 + (temp_c - 20) * 0.015));

// Calculate estimated compressive strength
let estimated_compressive_strength = base_strength_at_28d_20C * cement_type_modifier * curing_duration_factor * temperature_factor;

// Ensure strength is positive and within a reasonable range for concrete (e.g., 5 MPa to 100 MPa)
estimated_compressive_strength = Math.max(5, Math.min(100, estimated_compressive_strength));

return {
    water_cement_ratio: water_cement_ratio,
    estimated_compressive_strength: estimated_compressive_strength
};
