formula:
const lengthFt = inputs.slabLength;
const widthFt = inputs.slabWidth;
const thicknessIn = inputs.slabThickness;
const concreteCostPerYd3 = inputs.concreteCostPerCubicYard;
const rebarMeshCostPerSqFt = inputs.rebarMeshCostPerSqFt;
const wasteFactorPercent = inputs.wasteFactorPercent;

// Convert thickness from inches to feet
const thicknessFt = thicknessIn / 12;

// Calculate slab area in square feet
const slabAreaSqFt = lengthFt * widthFt;

// Calculate raw volume in cubic feet
const rawVolumeCuFt = slabAreaSqFt * thicknessFt;

// Convert raw volume to cubic yards
const rawVolumeCuYd = rawVolumeCuFt / 27;

// Apply waste factor
const effectiveWasteFactor = (1 + (wasteFactorPercent / 100));
const concreteVolumeWithWaste = rawVolumeCuYd * effectiveWasteFactor;

// Calculate concrete cost
const totalConcreteCost = concreteVolumeWithWaste * concreteCostPerYd3;

// Calculate rebar/mesh cost (only if rebarMeshCostPerSqFt is greater than 0)
const totalRebarMeshCost = rebarMeshCostPerSqFt > 0 ? slabAreaSqFt * rebarMeshCostPerSqFt : 0;

// Calculate total estimated cost
const totalEstimatedCost = totalConcreteCost + totalRebarMeshCost;

return {
  concreteVolume: concreteVolumeWithWaste,
  totalConcreteCost: totalConcreteCost,
  totalRebarMeshCost: totalRebarMeshCost,
  totalEstimatedCost: totalEstimatedCost
};
