const CUBIC_FEET_PER_CUBIC_YARD = 27;
const INCHES_PER_FOOT = 12;

const slabLength_ft = Math.max(0, parseFloat(inputs.slabLength_ft) || 0);
const slabWidth_ft = Math.max(0, parseFloat(inputs.slabWidth_ft) || 0);
const slabThickness_in = Math.max(0, parseFloat(inputs.slabThickness_in) || 0);
const wasteFactor_percent = Math.min(Math.max(0, parseFloat(inputs.wasteFactor_percent) || 0), 100);

const pricePerCubicYard = Math.max(0, parseFloat(inputs.pricePerCubicYard) || 0);
const pricePerBag = Math.max(0, parseFloat(inputs.pricePerBag) || 0);
const bagSize_cu_ft = Math.max(0.01, parseFloat(inputs.bagSize_cu_ft) || 0.67); // Default to 0.67 cu ft for an 80lb bag if not provided
const costByBags = String(inputs.costByBags).toLowerCase() === 'true';

const slabThickness_ft = slabThickness_in / INCHES_PER_FOOT;

const rawVolume_cu_ft = slabLength_ft * slabWidth_ft * slabThickness_ft;
const slabVolume_cu_yd = rawVolume_cu_ft / CUBIC_FEET_PER_CUBIC_YARD;

const wasteMultiplier = 1 + (wasteFactor_percent / 100);
const totalConcrete_cu_yd = slabVolume_cu_yd * wasteMultiplier;

const totalConcrete_cu_ft_with_waste = totalConcrete_cu_yd * CUBIC_FEET_PER_CUBIC_YARD;
const numBagsNeeded = Math.ceil(totalConcrete_cu_ft_with_waste / bagSize_cu_ft);

let estimatedMaterialCost = 0;
if (costByBags) {
    estimatedMaterialCost = numBagsNeeded * pricePerBag;
} else {
    estimatedMaterialCost = totalConcrete_cu_yd * pricePerCubicYard;
}

return {
    slabVolume_cu_yd: slabVolume_cu_yd,
    totalConcrete_cu_yd: totalConcrete_cu_yd,
    totalConcreteBags: numBagsNeeded,
    estimatedMaterialCost: estimatedMaterialCost
};
