function calculate({ length_ft, width_ft, thickness_in, height_ft, diameter_ft, num_items, waste_factor_percent }) {
    // Default values if inputs are not provided or are invalid
    length_ft = parseFloat(length_ft) || 0;
    width_ft = parseFloat(width_ft) || 0;
    thickness_in = parseFloat(thickness_in) || 0;
    height_ft = parseFloat(height_ft) || 0;
    diameter_ft = parseFloat(diameter_ft) || 0;
    num_items = parseFloat(num_items) || 1; // Default to 1 item
    waste_factor_percent = parseFloat(waste_factor_percent) || 5; // Default to 5% waste

    const CUBIC_FEET_PER_CUBIC_YARD = 27;
    const INCHES_PER_FOOT = 12;

    let base_volume_cf = 0;
    const thickness_ft = thickness_in / INCHES_PER_FOOT;

    // Prioritize cylindrical calculation if diameter and height are provided and valid
    if (diameter_ft > 0 && height_ft > 0) {
        const radius_ft = diameter_ft / 2;
        base_volume_cf = Math.PI * radius_ft * radius_ft * height_ft; // Volume of a cylinder
    }
    // Otherwise, attempt rectangular calculation if length, width, and an effective vertical dimension are provided
    else if (length_ft > 0 && width_ft > 0) {
        let effective_vertical_dimension_ft = 0;
        if (height_ft > 0) {
            effective_vertical_dimension_ft = height_ft;
        } else if (thickness_ft > 0) {
            effective_vertical_dimension_ft = thickness_ft;
        }

        if (effective_vertical_dimension_ft > 0) {
            base_volume_cf = length_ft * width_ft * effective_vertical_dimension_ft; // Volume of a rectangular prism
        }
    }
    // If no valid dimensions for a recognizable shape are provided, base_volume_cf remains 0.

    // Calculate total raw volume by multiplying by the number of identical items
    let total_volume_cf_raw = base_volume_cf * num_items;

    // Apply waste factor, ensuring it's not negative and capping it for safety (e.g., max 500% waste is absurd)
    const waste_factor_decimal = Math.min(500, Math.max(0, waste_factor_percent)) / 100; // Cap waste factor at 500% to prevent extreme values
    const total_volume_cf = total_volume_cf_raw * (1 + waste_factor_decimal);
    const total_volume_cy = total_volume_cf / CUBIC_FEET_PER_CUBIC_YARD;

    return {
        total_volume_cf: total_volume_cf,
        total_volume_cy: total_volume_cy
    };
}
