function calculateRisk(inputs) {
  const { materialSourcingVerificationLevel, laborPracticesComplianceScore, regulatoryAdherenceRecord, geopoliticalRiskFactor, supplyChainTierVisibility, supplierVettingRigour, projectCriticalityImpact, certificationsPresent } = inputs;

  // Input validation and scaling for risk (1=low risk, 5=high risk)
  // For inputs 1-5, convert to risk score (higher input score = lower risk, so invert)
  const matRisk = 6 - materialSourcingVerificationLevel; // 1 (best) -> 5 (worst)
  const labRisk = 6 - laborPracticesComplianceScore;   // 1 (best) -> 5 (worst)
  const regRisk = 6 - regulatoryAdherenceRecord;     // 1 (best) -> 5 (worst)
  const geoRisk = 6 - geopoliticalRiskFactor;        // 1 (best) -> 5 (worst)

  // Assign weights to core risk factors
  const WEIGHT_MAT = 0.25;
  const WEIGHT_LAB = 0.35;
  const WEIGHT_REG = 0.25;
  const WEIGHT_GEO = 0.15;

  // Calculate initial sub-scores (0-5 scale, 0=no risk, 5=max risk)
  let materialBaseRisk = matRisk;
  let laborBaseRisk = labRisk;
  let regulatoryGeopoliticalBaseRisk = (regRisk * 0.6 + geoRisk * 0.4); // Combine regulatory and geopolitical

  // Adjustments based on mitigating factors:
  // 1. Supply Chain Tier Visibility (higher visibility reduces risk)
  // Visibility: 1 (Tier 1 only) = no reduction, 2 (Tier 2) = 10% reduction, 3 (Tier 3+) = 20% reduction
  const visibilityReductionFactor = (supplyChainTierVisibility === 1) ? 1 : (supplyChainTierVisibility === 2) ? 0.9 : 0.8;
  materialBaseRisk *= visibilityReductionFactor;
  laborBaseRisk *= visibilityReductionFactor;
  regulatoryGeopoliticalBaseRisk *= visibilityReductionFactor;

  // 2. Supplier Vetting Rigour (more vetting reduces risk)
  // Vetting: 1 (basic) = no reduction, 2 (moderate) = 15% reduction, 3 (rigorous) = 25% reduction
  const vettingReductionFactor = (supplierVettingRigour === 1) ? 1 : (supplierVettingRigour === 2) ? 0.85 : 0.75;
  materialBaseRisk *= vettingReductionFactor;
  laborBaseRisk *= vettingReductionFactor;
  regulatoryGeopoliticalBaseRisk *= vettingReductionFactor;

  // 3. Certifications Present (reduces overall risk by a fixed percentage if present)
  const certificationReduction = certificationsPresent === 1 ? 0.8 : 1; // 20% reduction if certified
  materialBaseRisk *= certificationReduction;
  laborBaseRisk *= certificationReduction;
  regulatoryGeopoliticalBaseRisk *= certificationReduction;

  // Cap sub-scores to be within 0 and 5
  materialBaseRisk = Math.max(0, Math.min(5, materialBaseRisk));
  laborBaseRisk = Math.max(0, Math.min(5, laborBaseRisk));
  regulatoryGeopoliticalBaseRisk = Math.max(0, Math.min(5, regulatoryGeopoliticalBaseRisk));

  // Calculate weighted average of adjusted sub-scores
  let overallRiskScore = 
    (materialBaseRisk * WEIGHT_MAT) +
    (laborBaseRisk * WEIGHT_LAB) +
    (regulatoryGeopoliticalBaseRisk * (WEIGHT_REG + WEIGHT_GEO)); // Combine weights for combined regulatory/geopolitical

  // Normalize to 0-100 scale (where 5 is max risk)
  overallRiskScore = (overallRiskScore / 5) * 100;

  // Apply Project Criticality Impact (higher criticality increases the perceived risk or sets a higher floor)
  // 1 (low) = no multiplier
  // 2 (medium) = 1.1x multiplier, or min 20%
  // 3 (high) = 1.25x multiplier, or min 35%
  if (projectCriticalityImpact === 2) {
    overallRiskScore = Math.max(overallRiskScore * 1.1, 20); 
  } else if (projectCriticalityImpact === 3) {
    overallRiskScore = Math.max(overallRiskScore * 1.25, 35);
  }

  // Ensure overall score is within 0-100
  overallRiskScore = Math.max(0, Math.min(100, overallRiskScore));

  return {
    overallComplianceRiskScore: overallRiskScore,
    materialRiskRating: materialBaseRisk,
    laborRiskRating: laborBaseRisk,
    regulatoryGeopoliticalRiskRating: regulatoryGeopoliticalBaseRisk
  };
}
