const dailyOperatingCost = Number(inputs.dailyOperatingCost) || 0;
const dailyLaborCost = Number(inputs.dailyLaborCost) || 0;
const dailyEquipmentCost = Number(inputs.dailyEquipmentCost) || 0;
const dailyMaterialImpactCost = Number(inputs.dailyMaterialImpactCost) || 0;
const numDelayDays = Math.max(0, Math.round(Number(inputs.numDelayDays) || 0));
const overheadMarkupPercentage = Math.max(0, Number(inputs.overheadMarkupPercentage) || 0);
const reMobilizationCost = Number(inputs.reMobilizationCost) || 0;
const liquidatedDamagesPerDay = Number(inputs.liquidatedDamagesPerDay) || 0;

let totalScheduleImpactDays = numDelayDays;
let totalDirectDelayCost = 0;
let costPerDelayDay = 0;

if (numDelayDays === 0) {
  return {
    totalDelayCost: 0,
    costPerDelayDay: 0,
    totalScheduleImpactDays: 0
  };
}

// Step 1: Calculate the daily direct cost impact
const dailyDirectImpact = dailyOperatingCost + dailyLaborCost + dailyEquipmentCost + dailyMaterialImpactCost;

// Step 2: Calculate total direct costs for the delay period
const totalDirectDelayPeriodCost = dailyDirectImpact * numDelayDays;

// Step 3: Add re-mobilization cost if there's a significant delay (arbitrarily set to >1 day for re-mobilization trigger)
const totalReMobilizationCost = (numDelayDays > 1 && reMobilizationCost > 0) ? reMobilizationCost : 0;

// Step 4: Add liquidated damages
const totalLiquidatedDamages = liquidatedDamagesPerDay * numDelayDays;

// Step 5: Sum all base costs before overhead/profit
const subtotalCost = totalDirectDelayPeriodCost + totalReMobilizationCost + totalLiquidatedDamages;

// Step 6: Apply General Contractor overhead and profit markup
const overheadMultiplier = 1 + (overheadMarkupPercentage / 100);
totalDirectDelayCost = subtotalCost * overheadMultiplier;

// Step 7: Calculate average cost per delay day
costPerDelayDay = totalDirectDelayCost / numDelayDays;

return {
  totalDelayCost: totalDirectDelayCost,
  costPerDelayDay: costPerDelayDay,
  totalScheduleImpactDays: totalScheduleImpactDays
};
